import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NotificationBell from '../components/Notifications/NotificationBell.tsx';
import { MomentumTheme, MomentumComponents } from '../styles/MomentumTheme.ts';

interface EmployeeDashboardProps {
  user: any;
  onLogout: () => void;
}

interface Session {
  _id: string;
  sessionCategory: 'psychological' | 'financial';
  appointmentDate: string;
  duration: number;
  status: string;
  topic?: string;
  sessionType?: string;
  advisor: {
    name: string;
    email: string;
  };
  sessionRecord?: {
    sharedContent?: {
      sessionSummary?: string;
      generalTopics?: string[];
      nextSteps?: string[];
      followUpNeeded?: boolean;
      nextSessionDate?: string;
    };
  };
}

interface SessionStats {
  total: number;
  counselingCount: number;
  financialCount: number;
}

const EmployeeDashboard: React.FC<EmployeeDashboardProps> = ({ user, onLogout }) => {
  const [loading, setLoading] = useState(true);
  const [sessions, setSessions] = useState<Session[]>([]);
  const [stats, setStats] = useState<SessionStats>({ total: 0, counselingCount: 0, financialCount: 0 });
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedSession, setSelectedSession] = useState<Session | null>(null);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const response = await axios.get('http://localhost:3000/api/sessions/my-sessions', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setSessions(response.data.sessions || []);
      setStats({
        total: response.data.total || 0,
        counselingCount: response.data.counselingCount || 0,
        financialCount: response.data.financialCount || 0
      });
    } catch (error) {
      console.error('ë°ì´í„° ë¡œë“œ ì‹¤íŒ¨:', error);
      setSessions([]);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'scheduled': 
        return { 
          bg: `rgba(92, 126, 229, 0.1)`, 
          color: MomentumTheme.colors.primary 
        };
      case 'completed': 
        return { 
          bg: `rgba(16, 185, 129, 0.1)`, 
          color: MomentumTheme.colors.success 
        };
      case 'in-progress': 
        return { 
          bg: `rgba(227, 69, 74, 0.1)`, 
          color: MomentumTheme.colors.accent 
        };
      default: 
        return { 
          bg: `rgba(239, 68, 68, 0.1)`, 
          color: MomentumTheme.colors.error 
        };
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'scheduled': return 'ì˜ˆì •';
      case 'completed': return 'ì™„ë£Œ';
      case 'in-progress': return 'ì§„í–‰ì¤‘';
      case 'cancelled': return 'ì·¨ì†Œ';
      default: return 'ë¯¸ì¶œì„';
    }
  };

  if (loading) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        backgroundColor: '#f8fafc'
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ 
            width: '50px', 
            height: '50px', 
            border: '3px solid #f3f3f3',
            borderTop: '3px solid #3b82f6',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 20px'
          }}></div>
          <p style={{ color: '#6b7280', fontSize: '16px' }}>ì§ì› ëŒ€ì‹œë³´ë“œ ë¡œë”© ì¤‘...</p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ 
      minHeight: '100vh', 
      backgroundColor: MomentumTheme.colors.surface,
      fontFamily: MomentumTheme.typography.fontFamily.primary
    }}>
      {/* í—¤ë” */}
      <div style={MomentumComponents.header}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          maxWidth: '1200px',
          margin: '0 auto'
        }}>
          <div>
            <h1 style={{ 
              margin: '0 0 5px 0', 
              fontSize: '28px', 
              fontWeight: '700'
            }}>
              ðŸ¢ ë‚˜ì˜ EAP ëŒ€ì‹œë³´ë“œ
            </h1>
            <p style={{ 
              margin: '0', 
              fontSize: '16px', 
