import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MomentumTheme } from '../../styles/MomentumTheme.ts';

interface BookingModalProps {
  isOpen: boolean;
  onClose: () => void;
  sessionType: 'psychological' | 'financial';
  onSuccess?: () => void;
}

interface Counselor {
  _id: string;
  name: string;
  email: string;
  specialties?: string[];
  experience?: number;
  rating?: number;
  customRate?: number;
}

interface BookingData {
  counselorId: string;
  scheduledDate: string;
  duration: number;
  sessionType: string;
  format: 'face-to-face' | 'video-call' | 'phone-call';
  reason: string;
  notes: string;
}

const BookingModal: React.FC<BookingModalProps> = ({ isOpen, onClose, sessionType, onSuccess }) => {
  const [step, setStep] = useState(1);
  const [counselors, setCounselors] = useState<Counselor[]>([]);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [formData, setFormData] = useState<BookingData>({
    counselorId: '',
    scheduledDate: '',
    duration: 60,
    sessionType: sessionType === 'psychological' ? 'Í∞úÏù∏ÏÉÅÎã¥' : 'goal-planning',
    format: 'video-call',
    reason: '',
    notes: ''
  });

  useEffect(() => {
    if (isOpen) {
      loadCounselors();
    }
  }, [isOpen, sessionType]);

  const loadCounselors = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const endpoint = sessionType === 'psychological' 
        ? '/api/users/counselors' 
        : '/api/users?role=financial-advisor';

      const response = await axios.get(`http://localhost:3000${endpoint}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCounselors(response.data);
    } catch (error) {
      console.error('ÏÉÅÎã¥ÏÇ¨ Î™©Î°ù Î°úÎìú Ïã§Ìå®:', error);
      setCounselors([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async () => {
    try {
      setSubmitting(true);
      const token = localStorage.getItem('token');

      const endpoint = sessionType === 'psychological' 
        ? '/api/appointments'
        : '/api/financial-sessions';

      const submitData = sessionType === 'psychological' 
        ? {
            counselorId: formData.counselorId,
            scheduledDate: formData.scheduledDate,
            duration: formData.duration,
            type: 'individual',
            reason: formData.reason,
            notes: formData.notes
          }
        : {
            financialAdvisorId: formData.counselorId,
            scheduledDate: formData.scheduledDate,
            duration: formData.duration,
            sessionType: formData.sessionType,
            format: formData.format,
            preparation: {
              reason: formData.reason,
              notes: formData.notes
            }
          };

      await axios.post(`http://localhost:3000${endpoint}`, submitData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('ÏÉÅÎã¥ ÏòàÏïΩÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
      if (onSuccess) onSuccess();
      onClose();
      resetForm();
    } catch (error) {
      console.error('ÏòàÏïΩ Ïã§Ìå®:', error);
      console.error('Ïò§Î•ò ÏÉÅÏÑ∏:', error.response?.data);
      console.error('ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞:', submitData);
      console.error('HTTP ÏÉÅÌÉú:', error.response?.status);
      console.error('Ï†ÑÏ≤¥ ÏùëÎãµ:', error.response);

      let errorMessage = 'ÏòàÏïΩÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.';
      if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      } else if (error.response?.data?.errors) {
        errorMessage = error.response.data.errors.map(e => e.msg || e.message).join(', ');
      } else if (error.message) {
        errorMessage = error.message;
      }

      let detailError = 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•ò';
      try {
        if (error.response?.data) {
          detailError = JSON.stringify(error.response.data);
        } else if (error.message) {
          detailError = error.message;
        }
      } catch (e) {
        detailError = 'Ïò§Î•ò Ï†ïÎ≥¥ ÌååÏã± Ïã§Ìå®';
      }

      alert(`${errorMessage}\n\nÏÉÅÏÑ∏ Ïò§Î•ò: ${detailError}`);
    } finally {
      setSubmitting(false);
    }
  };

  const resetForm = () => {
    setStep(1);
    setFormData({
      counselorId: '',
      scheduledDate: '',
      duration: 60,
      sessionType: sessionType === 'psychological' ? 'Í∞úÏù∏ÏÉÅÎã¥' : 'goal-planning',
      format: 'video-call',
      reason: '',
      notes: ''
    });
  };

  const handleClose = () => {
    onClose();
    resetForm();
  };

  if (!isOpen) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '16px',
        width: '90%',
        maxWidth: '600px',
        maxHeight: '80vh',
        overflow: 'auto',
        position: 'relative'
      }}>
        {/* Ìó§Îçî */}
        <div style={{
          background: sessionType === 'psychological' 
            ? MomentumTheme.gradients.primary 
            : MomentumTheme.gradients.accent,
          color: 'white',
          padding: '24px',
          borderRadius: '16px 16px 0 0'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <h2 style={{ margin: '0 0 8px 0', fontSize: '24px', fontWeight: '700' }}>
                {sessionType === 'psychological' ? 'üß† Ïã¨Î¶¨ÏÉÅÎã¥ ÏòàÏïΩ' : 'üí∞ Ïû¨Î¨¥ÏÉÅÎã¥ ÏòàÏïΩ'}
              </h2>
              <p style={{ margin: '0', opacity: 0.9 }}>
                Îã®Í≥Ñ {step}/3
              </p>
            </div>
            <button
              onClick={handleClose}
              style={{
                background: 'rgba(255, 255, 255, 0.2)',
                border: 'none',
                color: 'white',
                fontSize: '24px',
                width: '40px',
                height: '40px',
                borderRadius: '50%',
                cursor: 'pointer'
              }}
            >
              √ó
            </button>
          </div>
        </div>

        {/* Î≥∏Î¨∏ */}
        <div style={{ padding: '32px' }}>
          {step === 1 && (
            <div>
              <h3 style={{ color: MomentumTheme.colors.text.primary, marginBottom: '20px' }}>
                ÏÉÅÎã¥ÏÇ¨ ÏÑ†ÌÉù
              </h3>

              {loading ? (
                <div style={{ textAlign: 'center', padding: '40px' }}>
                  <div>ÏÉÅÎã¥ÏÇ¨ Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</div>
                </div>
              ) : counselors.length === 0 ? (
                <div style={{ textAlign: 'center', padding: '40px', color: '#9ca3af' }}>
                  <div style={{ fontSize: '48px', marginBottom: '16px' }}>üë®‚Äç‚öïÔ∏è</div>
                  <p>ÌòÑÏû¨ Ïù¥Ïö© Í∞ÄÎä•Ìïú ÏÉÅÎã¥ÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
                </div>
              ) : (
                <div style={{ display: 'grid', gap: '12px' }}>
                  {counselors.map((counselor) => (
                    <div
                      key={counselor._id}
                      onClick={() => setFormData({ ...formData, counselorId: counselor._id })}
                      style={{
                        padding: '16px',
                        border: `2px solid ${formData.counselorId === counselor._id 
                          ? MomentumTheme.colors.primary 
                          : MomentumTheme.colors.gray[300]}`,
                        borderRadius: '12px',
                        cursor: 'pointer',
                        background: formData.counselorId === counselor._id 
                          ? `rgba(92, 126, 229, 0.1)` 
                          : 'white',
                        transition: 'all 0.3s ease'
                      }}
                    >
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
                        <div>
                          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '8px' }}>
                            <h4 style={{ 
                              margin: '0',
                              color: MomentumTheme.colors.text.primary,
                              fontWeight: '600'
                            }}>
                              {counselor.name}
                            </h4>
                            <span style={{
                              background: sessionType === 'psychological' 
                                ? MomentumTheme.gradients.primary 
                                : MomentumTheme.gradients.accent,
                              color: 'white',
                              padding: '2px 8px',
                              borderRadius: '12px',
                              fontSize: '12px',
                              fontWeight: '600'
                            }}>
                              {sessionType === 'psychological' ? 'Ïã¨Î¶¨ÏÉÅÎã¥ÏÇ¨' : 'Ïû¨Î¨¥ÏÉÅÎã¥ÏÇ¨'}
                            </span>
                          </div>
                          {counselor.specialties && counselor.specialties.length > 0 && (
                            <p style={{ 
                              margin: '0 0 4px 0',
                              fontSize: '14px',
                              color: MomentumTheme.colors.text.secondary
                            }}>
                              Ï†ÑÎ¨∏Î∂ÑÏïº: {counselor.specialties.join(', ')}
                            </p>
                          )}
                          {counselor.experience && (
                            <p style={{ 
                              margin: '0',
                              fontSize: '14px',
                              color: MomentumTheme.colors.text.secondary
                            }}>
                              Í≤ΩÎ†•: {counselor.experience}ÎÖÑ
                            </p>
                          )}
                        </div>
                        {counselor.customRate && (
                          <div style={{ textAlign: 'right' }}>
                            <p style={{ margin: '0', fontSize: '16px', fontWeight: '600', color: MomentumTheme.colors.primary }}>
                              {counselor.customRate.toLocaleString()}Ïõê
                            </p>
                            <p style={{ margin: '0', fontSize: '12px', color: MomentumTheme.colors.text.light }}>
                              60Î∂Ñ Í∏∞Ï§Ä
                            </p>
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {step === 2 && (
            <div>
              <h3 style={{ color: MomentumTheme.colors.text.primary, marginBottom: '20px' }}>
                ÏòàÏïΩ Ï†ïÎ≥¥ ÏûÖÎ†•
              </h3>

              <div style={{ display: 'grid', gap: '16px' }}>
                <div>
                  <label style={{ 
                    display: 'block',
                    marginBottom: '8px',
                    fontSize: '14px',
                    fontWeight: '600',
                    color: MomentumTheme.colors.text.primary
                  }}>
                    Ìù¨Îßù ÎÇ†Ïßú Î∞è ÏãúÍ∞Ñ
                  </label>
                  <input
                    type="datetime-local"
                    value={formData.scheduledDate}
                    onChange={(e) => setFormData({ ...formData, scheduledDate: e.target.value })}
                    min={new Date().toISOString().slice(0, 16)}
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: `2px solid ${MomentumTheme.colors.gray[300]}`,
                      borderRadius: '8px',
                      fontSize: '16px'
                    }}
                  />
                </div>

                <div>
                  <label style={{ 
                    display: 'block',
                    marginBottom: '8px',
                    fontSize: '14px',
                    fontWeight: '600',
                    color: MomentumTheme.colors.text.primary
                  }}>
                    ÏÉÅÎã¥ ÏãúÍ∞Ñ
                  </label>
                  <select
                    value={formData.duration}
                    onChange={(e) => setFormData({ ...formData, duration: parseInt(e.target.value) })}
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: `2px solid ${MomentumTheme.colors.gray[300]}`,
                      borderRadius: '8px',
                      fontSize: '16px'
                    }}
                  >
                    <option value={50}>50Î∂Ñ</option>
                    <option value={60}>60Î∂Ñ</option>
                    <option value={90}>90Î∂Ñ</option>
                  </select>
                </div>

                <div>
                  <label style={{ 
                    display: 'block',
                    marginBottom: '8px',
                    fontSize: '14px',
                    fontWeight: '600',
                    color: MomentumTheme.colors.text.primary
                  }}>
                    ÏÉÅÎã¥ Î∞©Ïãù
                  </label>
                  <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '8px' }}>
                    {[
                      { key: 'in-person', label: 'ÎåÄÎ©¥ÏÉÅÎã¥', icon: 'üë•' },
                      { key: 'video-call', label: 'ÌôîÏÉÅÌÜµÌôî', icon: 'üìπ' },
                      { key: 'phone-call', label: 'Ï†ÑÌôîÏÉÅÎã¥', icon: 'üìû' }
                    ].map((format) => (
                      <button
                        key={format.key}
                        type="button"
                        onClick={() => setFormData({ ...formData, format: format.key })}
                        style={{
                          padding: '12px',
                          border: `2px solid ${formData.format === format.key 
                            ? MomentumTheme.colors.primary 
                            : MomentumTheme.colors.gray[300]}`,
                          borderRadius: '8px',
                          background: formData.format === format.key 
                            ? `rgba(92, 126, 229, 0.1)` 
                            : 'white',
                          cursor: 'pointer',
                          fontSize: '14px',
                          fontWeight: formData.format === format.key ? '600' : '500'
                        }}
                      >
                        {format.icon} {format.label}
                      </button>
                    ))}
                  </div>
                </div>

                <div>
                  <label style={{ 
                    display: 'block',
                    marginBottom: '8px',
                    fontSize: '14px',
                    fontWeight: '600',
                    color: MomentumTheme.colors.text.primary
                  }}>
                    ÏÉÅÎã¥ ÏÇ¨Ïú†
                  </label>
                  <input
                    type="text"
                    value={formData.reason}
                    onChange={(e) => setFormData({ ...formData, reason: e.target.value })}
                    placeholder={sessionType === 'psychological' 
                      ? "Ïòà: ÏßÅÏû• Ïä§Ìä∏Î†àÏä§, ÎåÄÏù∏Í¥ÄÍ≥Ñ Îì±" 
                      : "Ïòà: Ïû¨Î¨¥Í≥ÑÌöç ÏàòÎ¶Ω, Ìà¨ÏûêÏÉÅÎã¥ Îì±"}
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: `2px solid ${MomentumTheme.colors.gray[300]}`,
                      borderRadius: '8px',
                      fontSize: '16px'
                    }}
                  />
                </div>
              </div>
            </div>
          )}

          {step === 3 && (
            <div>
              <h3 style={{ color: MomentumTheme.colors.text.primary, marginBottom: '20px' }}>
                Ï∂îÍ∞Ä ÏöîÏ≤≠ÏÇ¨Ìï≠
              </h3>

              <div>
                <label style={{ 
                  display: 'block',
                  marginBottom: '8px',
                  fontSize: '14px',
                  fontWeight: '600',
                  color: MomentumTheme.colors.text.primary
                }}>
                  ÏÉÅÎã¥ÏÇ¨ÏóêÍ≤å Ï†ÑÎã¨Ìï† Î©îÏãúÏßÄ (ÏÑ†ÌÉù)
                </label>
                <textarea
                  value={formData.notes}
