import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface SuperAdminDashboardProps {
  user: any;
  onLogout: () => void;
}

interface PlatformStats {
  totalCompanies: number;
  totalEmployees: number;
  totalCounselors: number;
  totalSessions: number;
  activeCompanies: number;
  monthlyRevenue: number;
  growthRate: number;
}

const SuperAdminDashboard: React.FC<SuperAdminDashboardProps> = ({ user, onLogout }) => {
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [platformStats, setPlatformStats] = useState<PlatformStats | null>(null);

  useEffect(() => {
    fetchSuperAdminData();
  }, []);

  const fetchSuperAdminData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const statsResponse = await axios.get('http://localhost:3000/api/super-admin/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPlatformStats(statsResponse.data);

    } catch (error) {
      console.error('Super admin data fetch failed:', error);
      setPlatformStats({
        totalCompanies: 15,
        totalEmployees: 2450,
        totalCounselors: 45,
        totalSessions: 3280,
        activeCompanies: 13,
        monthlyRevenue: 125000,
        growthRate: 15.6
      });
    } finally {
      setLoading(false);
    }
  };

  const tabs = [
    { id: 'overview', label: '대시보드 개요' },
    { id: 'companies', label: '기업 관리' },
    { id: 'counselors', label: '상담사 관리' },
    { id: 'assignments', label: '배정 관리' },
    { id: 'payments', label: '정산 관리' },
    { id: 'settings', label: '시스템 설정' }
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">수퍼 어드민 대시보드</h1>
              <p className="text-gray-600">전체 플랫폼 관리</p>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-600">안녕하세요, {user?.name || '관리자'}님</span>
              <button
                onClick={onLogout}
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md text-sm font-medium"
              >
                로그아웃
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation Tabs */}
      <nav className="bg-white border-b border-gray-200 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab.id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          {activeTab === 'overview' && (
            <div>
              {/* Platform Stats Cards */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                {loading ? (
                  [...Array(4)].map((_, i) => (
                    <div key={i} className="bg-white rounded-lg shadow p-6 animate-pulse">
                      <div className="h-4 bg-gray-300 rounded w-3/4 mb-2"></div>
                      <div className="h-8 bg-gray-300 rounded w-1/2"></div>
                    </div>
                  ))
                ) : (
                  platformStats && [
                    {
                      title: '전체 기업',
                      value: platformStats.totalCompanies,
                      subtitle: `활성 기업: ${platformStats.activeCompanies}`,
                      color: 'blue'
                    },
                    {
                      title: '전체 직원',
                      value: platformStats.totalEmployees.toLocaleString(),
                      subtitle: '등록된 직원 수',
                      color: 'green'
                    },
                    {
                      title: '전체 상담사',
                      value: platformStats.totalCounselors,
                      subtitle: '등록된 상담사 수',
                      color: 'purple'
                    },
                    {
                      title: '월 매출',
                      value: `₩${platformStats.monthlyRevenue.toLocaleString()}`,
                      subtitle: `성장률: ${platformStats.growthRate > 0 ? '+' : ''}${platformStats.growthRate}%`,
                      color: platformStats.growthRate > 0 ? 'green' : 'red'
                    }
                  ].map((card, index) => (
                    <div key={index} className="bg-white rounded-lg shadow p-6">
                      <h3 className="text-sm font-medium text-gray-500 mb-2">{card.title}</h3>
                      <p className={`text-2xl font-bold text-${card.color}-600`}>{card.value}</p>
                      <p className="text-sm text-gray-600 mt-1">{card.subtitle}</p>
                    </div>
                  ))
                )}
              </div>

              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-medium text-gray-900 mb-4">플랫폼 현황</h3>
                <div className="text-gray-600">
                  <p>• EAP 서비스 플랫폼이 정상 운영 중입니다.</p>
                  <p>• 모든 시스템이 정상적으로 작동하고 있습니다.</p>
                  <p>• 데이터베이스 연결 상태: 정상</p>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'companies' && (
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">기업 관리</h3>
              <p className="text-gray-500">기업 관리 기능이 로드 중입니다...</p>
            </div>
          )}

          {activeTab === 'counselors' && (
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">상담사 관리</h3>
              <p className="text-gray-500">상담사 관리 기능이 로드 중입니다...</p>
            </div>
          )}

          {activeTab === 'assignments' && (
            <div className="bg-white rounded-lg shadow p-6">
