import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Company {
  _id: string;
  name: string;
  domain: string;
  industry: string;
}

interface RegisterProps {
  onLogin: (user: any) => void;
}

const Register: React.FC<RegisterProps> = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: '',
    department: '',
    employeeId: '',
    companyId: ''
  });
  const [companies, setCompanies] = useState<Company[]>([]);
  const [departments, setDepartments] = useState<string[]>([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [loadingCompanies, setLoadingCompanies] = useState(true);

  useEffect(() => {
    fetchCompanies();
  }, []);

  const fetchCompanies = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/companies/public');
      setCompanies(response.data.companies);
    } catch (err) {
      console.error('ÌöåÏÇ¨ Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', err);
      setError('ÌöåÏÇ¨ Î™©Î°ùÏùÑ Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
    } finally {
      setLoadingCompanies(false);
    }
  };

  const fetchDepartments = async (companyId: string) => {
    try {
      const response = await axios.get(`http://localhost:3000/api/companies/${companyId}/departments`);
      setDepartments(response.data.departments || []);
    } catch (error) {
      console.error('Î∂ÄÏÑú Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
      // Í∏∞Î≥∏ Î∂ÄÏÑú Î™©Î°ù ÏÇ¨Ïö©
      setDepartments(['Í≤ΩÏòÅÏßÑ', 'ITÍ∞úÎ∞úÌåÄ', 'ÎßàÏºÄÌåÖÌåÄ', 'ÏòÅÏóÖÌåÄ', 'Ïù∏ÏÇ¨ÌåÄ', 'Ïû¨Î¨¥ÌåÄ', 'Ï¥ùÎ¨¥ÌåÄ', 'Í∏∞ÌöçÌåÄ', 'ÎîîÏûêÏù∏ÌåÄ', 'ÌíàÏßàÍ¥ÄÎ¶¨ÌåÄ']);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // ÌöåÏÇ¨ ÏÑ†ÌÉù Ïãú Ìï¥Îãπ ÌöåÏÇ¨Ïùò Î∂ÄÏÑú Î™©Î°ù Ï°∞Ìöå
    if (name === 'companyId' && value) {
      fetchDepartments(value);
      // ÌöåÏÇ¨ Î≥ÄÍ≤Ω Ïãú Î∂ÄÏÑú ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
      setFormData(prev => ({ ...prev, department: '' }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏
    if (formData.password !== formData.confirmPassword) {
      setError('ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
      setLoading(false);
      return;
    }

    // ÌïÑÏàò ÌïÑÎìú ÌôïÏù∏
    if (!formData.companyId) {
      setError('ÏÜåÏÜç ÌöåÏÇ¨Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post('http://localhost:3000/api/auth/register', {
        ...formData,
        role: 'employee'
      });

      localStorage.setItem('token', response.data.token);
      onLogin(response.data.user);
    } catch (err: any) {
      setError(err.response?.data?.message || 'ÌöåÏõêÍ∞ÄÏûÖÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  const inputStyle = {
    width: '100%',
    padding: '12px',
    border: '2px solid #e1e1e1',
    borderRadius: '6px',
    fontSize: '16px',
    boxSizing: 'border-box' as const
  };

  const labelStyle = {
    display: 'block',
    marginBottom: '8px',
    fontWeight: 'bold' as const,
    color: '#333'
  };

  if (loadingCompanies) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        backgroundColor: '#f5f5f5'
      }}>
        <div style={{ textAlign: 'center' }}>
          <h2 style={{ color: '#333' }}>ÌöåÏÇ¨ Ï†ïÎ≥¥ Î°úÎî© Ï§ë...</h2>
          <div style={{ 
            width: '50px', 
            height: '50px', 
            border: '3px solid #f3f3f3',
            borderTop: '3px solid #1976d2',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '20px auto'
          }}></div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ 
      minHeight: '100vh', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      backgroundColor: '#f5f5f5',
      fontFamily: 'Arial, sans-serif',
      padding: '20px'
    }}>
      <div style={{
        maxWidth: '500px',
        width: '100%',
        padding: '40px',
        backgroundColor: 'white',
        borderRadius: '10px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
      }}>
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <h1 style={{ color: '#333', fontSize: '28px', marginBottom: '10px' }}>
            üè¢ EAP Service
          </h1>
          <p style={{ color: '#666', fontSize: '16px' }}>
            ÏßÅÏõê ÌöåÏõêÍ∞ÄÏûÖ
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '20px' }}>
            <label style={labelStyle}>ÏÜåÏÜç ÌöåÏÇ¨ *</label>
            <select
              name="companyId"
              required
              value={formData.companyId}
              onChange={handleChange}
              style={inputStyle}
            >
              <option value="">ÌöåÏÇ¨Î•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
              {companies.map((company) => (
                <option key={company._id} value={company._id}>
                  {company.name} ({company.industry})
                </option>
              ))}
            </select>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={labelStyle}>Ïù¥Î©îÏùº *</label>
            <input
              type="email"
              name="email"
              required
              value={formData.email}
              onChange={handleChange}
              style={inputStyle}
              placeholder="Ïù¥Î©îÏùºÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={labelStyle}>Ïù¥Î¶Ñ *</label>
            <input
              type="text"
              name="name"
              required
              value={formData.name}
              onChange={handleChange}
              style={inputStyle}
              placeholder="Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={labelStyle}>Î∂ÄÏÑú *</label>
            <select
              name="department"
              required
              value={formData.department}
              onChange={handleChange}
              style={inputStyle}
              disabled={!formData.companyId || departments.length === 0}
            >
              <option value="">
                {!formData.companyId ? 'Î®ºÏ†Ä ÏÜåÏÜç ÌöåÏÇ¨Î•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî' : 
                 departments.length === 0 ? 'Î∂ÄÏÑú Î™©Î°ù Î°úÎî© Ï§ë...' : 'Î∂ÄÏÑúÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî'}
              </option>
              {departments.map((dept) => (
                <option key={dept} value={dept}>
                  {dept}
                </option>
              ))}
            </select>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={labelStyle}>ÏÇ¨ÏõêÎ≤àÌò∏</label>
            <input
              type="text"
              name="employeeId"
              value={formData.employeeId}
              onChange={handleChange}
              style={inputStyle}
              placeholder="ÏÇ¨ÏõêÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî (ÏÑ†ÌÉùÏÇ¨Ìï≠)"
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={labelStyle}>ÎπÑÎ∞ÄÎ≤àÌò∏ *</label>
            <input
              type="password"
              name="password"
              required
              minLength={6}
              value={formData.password}
              onChange={handleChange}
              style={inputStyle}
              placeholder="6Ïûê Ïù¥ÏÉÅ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={labelStyle}>ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏ *</label>
            <input
              type="password"
              name="confirmPassword"
              required
              minLength={6}
              value={formData.confirmPassword}
              onChange={handleChange}
              style={inputStyle}
              placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Îã§Ïãú ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            />
          </div>

          {error && (
            <div style={{
              backgroundColor: '#ffebee',
              color: '#c62828',
              padding: '12px',
              borderRadius: '6px',
              marginBottom: '20px',
              border: '1px solid #ffcdd2'
            }}>
              {error}
            </div>
          )}

          <button
            type="submit"
            disabled={loading}
            style={{
              width: '100%',
              padding: '14px',
              backgroundColor: loading ? '#ccc' : '#1976d2',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: loading ? 'not-allowed' : 'pointer',
              marginBottom: '20px'
            }}
          >
            {loading ? 'Í∞ÄÏûÖ Ï§ë...' : 'ÌöåÏõêÍ∞ÄÏûÖ'}
          </button>
        </form>

        <div style={{ textAlign: 'center' }}>
          <p style={{ color: '#666', margin: '10px 0' }}>
            Ïù¥ÎØ∏ Í≥ÑÏ†ïÏù¥ ÏûàÏúºÏã†Í∞ÄÏöî?
          </p>
          <a
            href="/login"
            style={{
              color: '#1976d2',
              textDecoration: 'none',
              fontWeight: 'bold'
            }}
          >
            Î°úÍ∑∏Ïù∏ÌïòÍ∏∞
          </a>
        </div>
      </div>
    </div>
  );
};

export default Register;
