import React, { useState } from 'react';
import { MomentumTheme } from '../../styles/MomentumTheme.ts';

interface FinancialGoal {
  _id?: string;
  title: string;
  targetAmount: number;
  targetDate: string;
  currentAmount: number;
  priority: 'high' | 'medium' | 'low';
  status: 'planning' | 'in-progress' | 'achieved' | 'suspended';
}

interface GoalsSectionProps {
  goals: FinancialGoal[];
  onAddGoal: (goal: Omit<FinancialGoal, '_id' | 'currentAmount' | 'status'>) => Promise<void>;
  onUpdateGoal: (goalId: string, updates: Partial<FinancialGoal>) => Promise<void>;
  onDeleteGoal: (goalId: string) => Promise<void>;
  isReadOnly?: boolean;
}

const GoalsSection: React.FC<GoalsSectionProps> = ({ 
  goals, 
  onAddGoal, 
  onUpdateGoal, 
  onDeleteGoal, 
  isReadOnly = false 
}) => {
  const [showForm, setShowForm] = useState(false);
  const [editingGoal, setEditingGoal] = useState<FinancialGoal | null>(null);
  const [updatingProgress, setUpdatingProgress] = useState<string | null>(null);
  const [progressAmount, setProgressAmount] = useState('');
  const [formData, setFormData] = useState({
    title: '',
    targetAmount: '',
    targetDate: '',
    priority: 'medium' as 'high' | 'medium' | 'low'
  });

  const toWonUnits = (value: number): string => (value / 10000).toString();
  const fromWonUnits = (value: string): number => parseFloat(value || '0') * 10000;
  const formatWonUnits = (value: number): string => 
    new Intl.NumberFormat('ko-KR').format(value / 10000) + 'ÎßåÏõê';

  const resetForm = () => {
    setFormData({ title: '', targetAmount: '', targetDate: '', priority: 'medium' });
    setEditingGoal(null);
    setShowForm(false);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // ÏûÖÎ†• Í≤ÄÏ¶ù
    if (!formData.title.trim()) {
      alert('Î™©ÌëúÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    if (!formData.targetAmount || parseFloat(formData.targetAmount) <= 0) {
      alert('Î™©Ìëú Í∏àÏï°ÏùÑ Ïò¨Î∞îÎ•¥Í≤å ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    if (!formData.targetDate) {
      alert('Î™©Ìëú ÎÇ†ÏßúÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      const goalData = {
        title: formData.title,
        targetAmount: fromWonUnits(formData.targetAmount),
        targetDate: formData.targetDate,
        priority: formData.priority
      };

      if (editingGoal) {
        await onUpdateGoal(editingGoal._id!, goalData);
      } else {
        await onAddGoal(goalData);
      }

      resetForm();
    } catch (error) {
      console.error('Î™©Ìëú Ï†ÄÏû• Ïã§Ìå®:', error);
      alert('Î™©Ìëú Ï†ÄÏû• Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ' + (error instanceof Error ? error.message : 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•ò'));
    }
  };

  const startEdit = (goal: FinancialGoal) => {
    setEditingGoal(goal);
    setFormData({
      title: goal.title,
      targetAmount: toWonUnits(goal.targetAmount),
      targetDate: goal.targetDate.split('T')[0],
      priority: goal.priority
    });
    setShowForm(true);
  };

  const startProgressUpdate = (goal: FinancialGoal) => {
    setUpdatingProgress(goal._id!);
    setProgressAmount(toWonUnits(goal.currentAmount));
  };

  const handleProgressUpdate = async (goalId: string) => {
    try {
      const currentAmount = fromWonUnits(progressAmount);

      // ÏûÖÎ†• Í≤ÄÏ¶ù
      if (!progressAmount || isNaN(currentAmount) || currentAmount < 0) {
        alert('Ïò¨Î∞îÎ•∏ Í∏àÏï°ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        return;
      }

      await onUpdateGoal(goalId, { currentAmount });

      setUpdatingProgress(null);
      setProgressAmount('');
    } catch (error) {
      console.error('Îã¨ÏÑ±Î•† ÏóÖÎç∞Ïù¥Ìä∏ Ïã§Ìå®:', error);
      alert('Îã¨ÏÑ±Î•† ÏóÖÎç∞Ïù¥Ìä∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  const cancelProgressUpdate = () => {
    setUpdatingProgress(null);
    setProgressAmount('');
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return '#ef4444';
      case 'medium': return '#f59e0b';
      case 'low': return '#10b981';
      default: return '#6b7280';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'achieved': return '#10b981';
      case 'in-progress': return '#3b82f6';
      case 'planning': return '#6b7280';
      case 'suspended': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const calculateProgress = (goal: FinancialGoal) => {
    return goal.targetAmount > 0 ? (goal.currentAmount / goal.targetAmount) * 100 : 0;
  };

  return (
    <div>
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        marginBottom: '24px' 
      }}>
        <h3 style={{ 
          color: MomentumTheme.colors.primary, 
          margin: '0',
          fontSize: '20px',
          fontWeight: '600'
        }}>
          üéØ Ïû¨Î¨¥ Î™©Ìëú ({goals.length}Í∞ú)
        </h3>

        {!isReadOnly && (
          <button
            onClick={() => setShowForm(true)}
            style={{
              padding: '10px 20px',
              background: MomentumTheme.gradients.primary,
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '500'
            }}
          >
            + ÏÉà Î™©Ìëú Ï∂îÍ∞Ä
          </button>
        )}
      </div>

      {/* Î™©Ìëú Ï∂îÍ∞Ä/ÏàòÏ†ï Ìèº */}
      {showForm && (
        <div style={{
          background: MomentumTheme.colors.gray[50],
          padding: '24px',
          borderRadius: '12px',
          marginBottom: '24px'
        }}>
          <h4 style={{ color: MomentumTheme.colors.text.primary, marginBottom: '16px' }}>
            {editingGoal ? 'Î™©Ìëú ÏàòÏ†ï' : 'ÏÉà Î™©Ìëú Ï∂îÍ∞Ä'}
          </h4>

          <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '16px' }}>
            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                fontSize: '14px',
                fontWeight: '600',
                color: MomentumTheme.colors.text.primary
              }}>
                Î™©ÌëúÎ™Ö
              </label>
              <input
                type="text"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                placeholder="Ïòà: ÎÇ¥ Ïßë ÎßàÎ†®, ÎÖ∏ÌõÑ ÏûêÍ∏à"
                required
                style={{
                  width: '100%',
                  padding: '12px',
                  border: `2px solid ${MomentumTheme.colors.gray[300]}`,
                  borderRadius: '8px',
                  fontSize: '16px'
                }}
              />
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px' }}>
              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  fontSize: '14px',
                  fontWeight: '600',
                  color: MomentumTheme.colors.text.primary
                }}>
                  Î™©Ìëú Í∏àÏï° (ÎßåÏõê)
                </label>
                <input
                  type="number"
                  value={formData.targetAmount}
                  onChange={(e) => setFormData({ ...formData, targetAmount: e.target.value })}
                  placeholder="0"
                  required
                  min="0"
                  step="0.01"
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: `2px solid ${MomentumTheme.colors.gray[300]}`,
                    borderRadius: '8px',
                    fontSize: '16px'
                  }}
                />
              </div>

              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  fontSize: '14px',
                  fontWeight: '600',
                  color: MomentumTheme.colors.text.primary
                }}>
                  Î™©Ìëú ÎÇ†Ïßú
                </label>
                <input
                  type="date"
                  value={formData.targetDate}
                  onChange={(e) => setFormData({ ...formData, targetDate: e.target.value })}
                  required
                  min={new Date().toISOString().split('T')[0]}
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: `2px solid ${MomentumTheme.colors.gray[300]}`,
                    borderRadius: '8px',
                    fontSize: '16px'
                  }}
                />
              </div>

              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  fontSize: '14px',
                  fontWeight: '600',
                  color: MomentumTheme.colors.text.primary
                }}>
                  Ïö∞ÏÑ†ÏàúÏúÑ
                </label>
                <select
                  value={formData.priority}
                  onChange={(e) => setFormData({ ...formData, priority: e.target.value as any })}
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: `2px solid ${MomentumTheme.colors.gray[300]}`,
                    borderRadius: '8px',
                    fontSize: '16px'
                  }}
                >
                  <option value="high">ÎÜíÏùå</option>
                  <option value="medium">Î≥¥ÌÜµ</option>
                  <option value="low">ÎÇÆÏùå</option>
                </select>
              </div>
            </div>

            <div style={{ display: 'flex', gap: '12px', justifyContent: 'flex-end' }}>
              <button
                type="button"
                onClick={resetForm}
                style={{
                  padding: '10px 20px',
                  background: MomentumTheme.colors.gray[200],
                  color: MomentumTheme.colors.text.secondary,
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '14px'
                }}
              >
                Ï∑®ÏÜå
              </button>
              <button
                type="submit"
                style={{
                  padding: '10px 20px',
                  background: MomentumTheme.gradients.primary,
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: '500'
                }}
              >
                {editingGoal ? 'ÏàòÏ†ï' : 'Ï∂îÍ∞Ä'}
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Î™©Ìëú Î™©Î°ù */}
      {goals.length === 0 ? (
        <div style={{
          textAlign: 'center',
          padding: '60px 20px',
          color: MomentumTheme.colors.text.light
        }}>
          <div style={{ fontSize: '48px', marginBottom: '16px' }}>üéØ</div>
          <h3 style={{ color: MomentumTheme.colors.text.secondary, marginBottom: '8px' }}>
            Ïû¨Î¨¥ Î™©ÌëúÎ•º ÏÑ§Ï†ïÌïòÏÑ∏Ïöî
          </h3>
          <p>Íµ¨Ï≤¥Ï†ÅÏù∏ Ïû¨Î¨¥ Î™©ÌëúÎ•º ÏÑ§Ï†ïÌïòÍ≥† Îã¨ÏÑ± Í≥ºÏ†ïÏùÑ Í¥ÄÎ¶¨Ìï† Ïàò ÏûàÏäµÎãàÎã§.</p>
        </div>
      ) : (
        <div style={{ display: 'grid', gap: '16px' }}>
          {goals.map((goal) => {
            const progress = calculateProgress(goal);
            const isOverdue = new Date(goal.targetDate) < new Date() && goal.status !== 'achieved';

            return (
              <div
                key={goal._id}
                style={{
                  border: `2px solid ${isOverdue ? '#fef3c7' : MomentumTheme.colors.gray[200]}`,
                  borderRadius: '12px',
                  padding: '20px',
                  background: isOverdue ? '#fffbeb' : 'white'
                }}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '12px' }}>
                  <div>
                    <h4 style={{ 
                      margin: '0 0 8px 0',
                      color: MomentumTheme.colors.text.primary,
                      fontSize: '18px',
                      fontWeight: '600'
                    }}>
                      {goal.title}
                    </h4>

                    <div style={{ display: 'flex', gap: '12px', alignItems: 'center', marginBottom: '8px' }}>
                      <span style={{
                        background: getPriorityColor(goal.priority),
                        color: 'white',
                        padding: '2px 8px',
                        borderRadius: '12px',
                        fontSize: '12px',
                        fontWeight: '600'
                      }}>
                        {goal.priority === 'high' ? 'ÎÜíÏùå' : 
                         goal.priority === 'medium' ? 'Î≥¥ÌÜµ' : 'ÎÇÆÏùå'}
                      </span>

                      <span style={{
                        background: getStatusColor(goal.status),
                        color: 'white',
                        padding: '2px 8px',
                        borderRadius: '12px',
                        fontSize: '12px',
                        fontWeight: '600'
                      }}>
                        {goal.status === 'planning' ? 'Í≥ÑÌöç' :
                         goal.status === 'in-progress' ? 'ÏßÑÌñâÏ§ë' :
                         goal.status === 'achieved' ? 'Îã¨ÏÑ±' : 'Ï§ëÎã®'}
                      </span>

                      {isOverdue && (
                        <span style={{
                          background: '#f59e0b',
                          color: 'white',
                          padding: '2px 8px',
                          borderRadius: '12px',
                          fontSize: '12px',
                          fontWeight: '600'
                        }}>
                          Í∏∞Ìïú Ï¥àÍ≥º
                        </span>
                      )}
                    </div>

                    <p style={{ 
                      margin: '0 0 4px 0',
                      fontSize: '14px',
                      color: MomentumTheme.colors.text.secondary 
                    }}>
                      Î™©Ìëú: {formatWonUnits(goal.targetAmount)} | ÌòÑÏû¨: {formatWonUnits(goal.currentAmount)}
                    </p>

                    <p style={{ 
                      margin: '0',
                      fontSize: '14px',
                      color: MomentumTheme.colors.text.secondary 
                    }}>
                      Î™©ÌëúÏùº: {new Date(goal.targetDate).toLocaleDateString('ko-KR')}
                    </p>
                  </div>

                  {!isReadOnly && (
                    <div style={{ display: 'flex', gap: '8px' }}>
                      <button
                        onClick={() => startEdit(goal)}
                        style={{
                          padding: '6px 12px',
                          background: MomentumTheme.colors.gray[100],
                          border: `1px solid ${MomentumTheme.colors.gray[300]}`,
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '12px'
                        }}
                      >
                        ÏàòÏ†ï
                      </button>
                      <button
                        onClick={() => onDeleteGoal(goal._id!)}
                        style={{
                          padding: '6px 12px',
                          background: '#fee2e2',
                          border: '1px solid #fecaca',
                          color: '#dc2626',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '12px'
                        }}
                      >
                        ÏÇ≠Ï†ú
                      </button>
                    </div>
                  )}
                </div>

                {/* ÏßÑÌñâÎ•† Î∞î */}
                <div style={{ marginTop: '16px' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                    <span style={{ fontSize: '14px', color: MomentumTheme.colors.text.secondary }}>
                      ÏßÑÌñâÎ•†
                    </span>
                    <span style={{ fontSize: '14px', fontWeight: '600', color: MomentumTheme.colors.text.primary }}>
                      {progress.toFixed(1)}%
                    </span>
                  </div>

                  <div style={{
                    width: '100%',
                    height: '8px',
                    background: MomentumTheme.colors.gray[200],
                    borderRadius: '4px',
                    overflow: 'hidden'
                  }}>
                    <div style={{
                      width: `${Math.min(progress, 100)}%`,
                      height: '100%',
                      background: progress >= 100 ? '#10b981' : MomentumTheme.gradients.primary,
                      transition: 'width 0.3s ease'
                    }} />
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default GoalsSection;
