          employeeCount: currentEmployeeCount,
          avgSalary,
          dailyProductivity,
          preEapMetrics: {
            turnoverRate: 15,
            absenteeismDays: currentEmployeeCount * 8
          }
        },
        generatedAt: new Date(),
        reportType: 'cost'
      };
      setCurrentReport(demoReport);
