sourcesContent:
import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface SuperAdminDashboardProps {
  user: any;
  onLogout: () => void;
}

interface Company {
  _id: string;
  name: string;
  domain: string;
  industry: string;
  employeeCount: number;
  activeCounselors: number;
  monthlyUsage: number;
  plan: 'basic' | 'premium' | 'enterprise';
  status: 'active' | 'inactive' | 'trial';
  createdAt: string;
  lastActivity: string;
  settings: {
    maxEmployees: number;
    maxCounselors: number;
    features: string[];
  };
}

interface Counselor {
  _id: string;
  name: string;
  email: string;
  phone: string;
  specialties: string[];
  licenseNumber: string;
  experience: number;
  rates: {
    faceToFace: number;
    phoneVideo: number;
    chat: number;
  };
  maxDailyAppointments: number;
  isActive: boolean;
  totalSessions: number;
  rating: number;
  createdAt: string;
}

interface PendingAssignment {
  _id: string;
  employee: {
    name: string;
    email: string;
    company: string;
    department: string;
  };
  topic: string;
  urgencyLevel: 'low' | 'medium' | 'high' | 'critical';
  counselingMethod: 'faceToFace' | 'phoneVideo' | 'chat';
  sessionType: string;
  requestedDate: string;
  assignmentStatus: 'pending' | 'assigned' | 'confirmed';
  createdAt: string;
}

interface CounselorPayment {
  _id: string;
  counselor: {
    _id: string;
    name: string;
  };
  year: number;
  month: number;
  summary: {
    totalSessions: number;
    faceToFaceSessions: number;
    phoneVideoSessions: number;
    chatSessions: number;
    totalAmount: number;
    taxAmount: number;
    netAmount: number;
  };
  status: 'pending' | 'approved' | 'paid' | 'dispute';
  approvedAt?: string;
  paidAt?: string;
}

interface PlatformStats {
  totalCompanies: number;
  totalEmployees: number;
  totalCounselors: number;
  totalSessions: number;
  activeCompanies: number;
  monthlyRevenue: number;
  growthRate: number;
}

interface CounselingCenter {
  _id: string;
  name: string;
  type: 'center' | 'independent';
  description: string;
  adminUser?: {
    name: string;
    email: string;
  };
  counselors: any[];
  businessLicense: string;
  address: {
    street: string;
    city: string;
    state: string;
    zipCode: string;
  };
  contact: {
    phone: string;
    email: string;
    website: string;
  };
  specialties: string[];
  isActive: boolean;
  totalSessions: number;
  rating: number;
  createdAt: string;
  activeCounselor: number;
  totalCounselors: number;
}

const SuperAdminDashboard: React.FC<SuperAdminDashboardProps> = ({ user, onLogout }) => {
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [companies, setCompanies] = useState<Company[]>([]);
  const [counselors, setCounselors] = useState<Counselor[]>([]);
  const [pendingAssignments, setPendingAssignments] = useState<PendingAssignment[]>([]);
  const [counselorPayments, setCounselorPayments] = useState<CounselorPayment[]>([]);
  const [counselingCenters, setCounselingCenters] = useState<CounselingCenter[]>([]);
  const [platformStats, setPlatformStats] = useState<PlatformStats | null>(null);
  const [apiStatus, setApiStatus] = useState('testing');
  const [testResult, setTestResult] = useState('');

  // 회사 생성 폼
  const [showAddCompany, setShowAddCompany] = useState(false);
  // 상담사 생성 폼
  const [showAddCounselor, setShowAddCounselor] = useState(false);
  // 상담사 편집 폼
  const [showEditCounselor, setShowEditCounselor] = useState(false);
  const [editingCounselor, setEditingCounselor] = useState<Counselor | null>(null);
  const [showSessionDetails, setShowSessionDetails] = useState(false);
  const [selectedCounselorSessions, setSelectedCounselorSessions] = useState<any[]>([]);
  const [loadingSessions, setLoadingSessions] = useState(false);
  const [counselorSettlements, setCounselorSettlements] = useState<{[key: string]: any}>({});
  const [loadingSettlements, setLoadingSettlements] = useState(false);
  // 상담센터 생성 폼
  const [showAddCenter, setShowAddCenter] = useState(false);
  const [newCenter, setNewCenter] = useState({
    name: '',
    description: '',
    adminUserEmail: '',
    businessLicense: '',
    address: {
      street: '',
      city: '',
      state: '',
      zipCode: ''
    },
    contact: {
      phone: '',
      email: '',
      website: ''
    },
    specialties: ['']
  });

  // 단가 설정 모달
  const [showRateModal, setShowRateModal] = useState(false);
  const [selectedCounselorId, setSelectedCounselorId] = useState('');
  const [rateSettings, setRateSettings] = useState({
    useSystemRate: true,
    customRate: 50000
  });
  const [newCounselor, setNewCounselor] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    specialties: [''],
    licenseNumber: '',
    experience: 0,
    rates: {
      faceToFace: 80000,
      phoneVideo: 60000,
      chat: 40000
    },
    maxDailyAppointments: 8
  });
  // 상담사 배정 모달
  const [selectedAssignment, setSelectedAssignment] = useState<PendingAssignment | null>(null);
  const [showAssignmentModal, setShowAssignmentModal] = useState(false);

  // 정산 관리 상태
  const [paymentViewType, setPaymentViewType] = useState<'counselor' | 'center' | 'both'>('counselor');
  const [paymentPeriod, setPaymentPeriod] = useState('2024-08');
  const [newCompany, setNewCompany] = useState({
    name: '',
    domain: '',
    industry: '',
    plan: 'basic' as 'basic' | 'premium' | 'enterprise',
    maxEmployees: 100,
    maxCounselors: 5,
    annualCounselingLimit: 12,
    adminEmail: '',
    adminName: ''
  });

  useEffect(() => {
    fetchSuperAdminData();
  }, []);

  // 정산 관리 탭이 활성화되고 상담사 데이터가 있을 때 정산 정보 조회
  useEffect(() => {
    if (activeTab === 'payments' && counselors.length > 0) {
      fetchAllCounselorSettlements();
    }
  }, [activeTab, paymentPeriod, counselors]);

  const fetchSuperAdminData = async () => {
    try {
      await testApiConnection();

      const token = localStorage.getItem('token');

      // 플랫폼 통계 조회
      const statsResponse = await axios.get('http://localhost:3000/api/super-admin/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPlatformStats(statsResponse.data);

      // 회사 목록 조회
      const companiesResponse = await axios.get('http://localhost:3000/api/super-admin/companies', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCompanies(companiesResponse.data.companies);

      // 상담사 목록 조회
      const counselorsResponse = await axios.get('http://localhost:3000/api/counselors', {
