                        <div style={{ 
                          display: 'grid', 
                          gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))', 
                          gap: '12px', 
                          marginBottom: '16px',
                          padding: '16px',
                          backgroundColor: '#f8fafc',
                          borderRadius: '8px'
                        }}>
                          <div>
                            <div style={{ fontSize: '12px', color: '#6b7280', marginBottom: '4px' }}>총 상담료</div>
                            <div style={{ fontSize: '16px', fontWeight: '600', color: '#111827' }}>
                              ₩{(payment.summary?.totalAmount || 0).toLocaleString()}
                            </div>
                          </div>
                          <div>
                            <div style={{ fontSize: '12px', color: '#6b7280', marginBottom: '4px' }}>세금 ({taxRate}%)</div>
                            <div style={{ fontSize: '16px', fontWeight: '600', color: '#dc2626' }}>
                              -₩{taxAmount.toLocaleString()}
                            </div>
                          </div>
                          <div>
                            <div style={{ fontSize: '12px', color: '#6b7280', marginBottom: '4px' }}>실 수령액</div>
                            <div style={{ fontSize: '18px', fontWeight: 'bold', color: '#059669' }}>
                              ₩{netAmount.toLocaleString()}
                            </div>
                          </div>
                        </div>

                        {/* 액션 버튼들 */}
                        <div style={{ display: 'flex', gap: '8px', justifyContent: 'flex-end' }}>
                          <button 
                            onClick={() => handleDownloadPaymentReport(payment)}
                            style={{
                              background: '#6b7280',
                              color: 'white',
                              border: 'none',
                              padding: '6px 12px',
                              borderRadius: '4px',
                              cursor: 'pointer',
                              fontSize: '12px'
                            }}
                          >
                            📄 정산서 다운로드
                          </button>
                          {payment.status === 'pending' && (
                            <>
                              <button 
                                onClick={() => handleRejectPayment(payment._id)}
                                style={{
                                  background: '#dc2626',
                                  color: 'white',
                                  border: 'none',
                                  padding: '6px 12px',
                                  borderRadius: '4px',
                                  cursor: 'pointer',
                                  fontSize: '12px'
                                }}
                              >
                                거절
                              </button>
                              <button 
                                onClick={() => handleApprovePayment(payment._id)}
                                style={{
                                  background: '#16a34a',
                                  color: 'white',
                                  border: 'none',
                                  padding: '6px 12px',
                                  borderRadius: '4px',
                                  cursor: 'pointer',
                                  fontSize: '12px'
                                }}
                              >
                                승인
                              </button>
                            </>
                          )}
                          {payment.status === 'processing' && (
                            <button 
                              onClick={() => handleCompletePayment(payment._id)}
                              style={{
                                background: '#2563eb',
                                color: 'white',
                                border: 'none',
                                padding: '6px 12px',
                                borderRadius: '4px',
                                cursor: 'pointer',
                                fontSize: '12px'
                              }}
                            >
                              지급 완료
                            </button>
                          )}
                          <button 
                            onClick={() => handleViewPaymentDetail(payment)}
                            style={{
                              background: '#2563eb',
                              color: 'white',
                              border: 'none',
                              padding: '6px 12px',
                              borderRadius: '4px',
                              cursor: 'pointer',
                              fontSize: '12px'
                            }}
                          >
                            상세보기
                          </button>
                        </div>
                      </div>
                    );
                  })}
                </div>
              ) : (
                <p style={{ color: '#6b7280', textAlign: 'center', padding: '32px' }}>정산 내역이 없습니다.</p>
              )}

              {/* 정산 상세보기 모달 */}
              {viewingPaymentDetail && (
                <div style={{
                  position: 'fixed',
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  backgroundColor: 'rgba(0, 0, 0, 0.5)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  zIndex: 1000
                }}>
                  <div style={{
                    backgroundColor: 'white',
                    borderRadius: '12px',
                    padding: '24px',
                    width: '700px',
                    maxHeight: '80vh',
                    overflow: 'auto'
                  }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
                      <h3 style={{ margin: '0', fontSize: '20px', fontWeight: '600' }}>
                        정산 상세 내역
                      </h3>
                      <button 
                        onClick={() => setViewingPaymentDetail(null)}
                        style={{
                          background: 'none',
                          border: 'none',
                          fontSize: '24px',
                          cursor: 'pointer',
                          color: '#6b7280'
                        }}
                      >
                        ×
                      </button>
                    </div>

                    {/* 상담사 기본 정보 */}
                    <div style={{ marginBottom: '24px', padding: '16px', backgroundColor: '#f8fafc', borderRadius: '8px' }}>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '8px' }}>
                        <h4 style={{ margin: '0', fontSize: '18px', fontWeight: '600' }}>
                          {viewingPaymentDetail.counselor?.name || '상담사 정보 없음'}
                        </h4>
                        <span style={{
                          padding: '4px 12px',
                          borderRadius: '20px',
                          fontSize: '12px',
                          fontWeight: '500',
                          background: viewingPaymentDetail.status === 'pending' ? '#fef3c7' : 
                                     viewingPaymentDetail.status === 'processing' ? '#dbeafe' : 
                                     viewingPaymentDetail.status === 'completed' ? '#dcfce7' : '#fee2e2',
                          color: viewingPaymentDetail.status === 'pending' ? '#f59e0b' : 
                                 viewingPaymentDetail.status === 'processing' ? '#2563eb' : 
                                 viewingPaymentDetail.status === 'completed' ? '#16a34a' : '#dc2626'
                        }}>
                          {viewingPaymentDetail.status === 'pending' ? '정산 대기' : 
                           viewingPaymentDetail.status === 'processing' ? '정산 진행' : 
                           viewingPaymentDetail.status === 'completed' ? '정산 완료' : '이의 신청'}
                        </span>
                      </div>
                      <div style={{ fontSize: '14px', color: '#6b7280' }}>
                        📅 {viewingPaymentDetail.year}년 {viewingPaymentDetail.month}월 정산
                      </div>
                    </div>

                    {/* 상세 세션 목록 */}
                    <div style={{ marginBottom: '24px' }}>
                      <h5 style={{ margin: '0 0 16px 0', fontSize: '16px', fontWeight: '500' }}>
                        상담 세션 내역 ({viewingPaymentDetail.sessions?.length || 0}건)
                      </h5>
                      <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
                        {viewingPaymentDetail.sessions && viewingPaymentDetail.sessions.length > 0 ? (
                          <div style={{ display: 'grid', gap: '8px' }}>
                            {viewingPaymentDetail.sessions.map((session: any, index: number) => (
                              <div key={index} style={{
                                padding: '12px',
                                border: '1px solid #e5e7eb',
                                borderRadius: '8px',
                                backgroundColor: '#fafafa'
                              }}>
                                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
                                  <div>
                                    <div style={{ fontWeight: '500', marginBottom: '4px' }}>
                                      {session.company} - {session.employeeName}
                                    </div>
                                    <div style={{ fontSize: '14px', color: '#6b7280' }}>
                                      📅 {new Date(session.date).toLocaleDateString('ko-KR')} • 
                                      {session.method === 'faceToFace' ? ' 대면상담' : 
                                       session.method === 'phoneVideo' ? ' 화상/전화' : ' 채팅상담'} • 
                                      {session.duration}분
                                    </div>
                                  </div>
                                  <div style={{ textAlign: 'right' }}>
                                    <div style={{ fontWeight: '500' }}>
                                      ₩{session.amount.toLocaleString()}
                                    </div>
                                  </div>
                                </div>
                              </div>
                            ))}
                          </div>
                        ) : (
                          <div style={{ textAlign: 'center', padding: '40px', color: '#6b7280' }}>
                            상담 세션 내역이 없습니다.
                          </div>
                        )}
                      </div>
                    </div>

                    {/* 금액 요약 */}
                    <div style={{ 
                      padding: '16px',
                      backgroundColor: '#f8fafc',
                      borderRadius: '8px',
                      marginBottom: '20px'
                    }}>
                      <h5 style={{ margin: '0 0 12px 0', fontSize: '16px', fontWeight: '500' }}>정산 요약</h5>
                      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px' }}>
                        <div>
                          <div style={{ fontSize: '12px', color: '#6b7280', marginBottom: '4px' }}>총 상담료</div>
                          <div style={{ fontSize: '16px', fontWeight: '600' }}>
                            ₩{(viewingPaymentDetail.summary?.totalAmount || 0).toLocaleString()}
                          </div>
                        </div>
                        <div>
                          <div style={{ fontSize: '12px', color: '#6b7280', marginBottom: '4px' }}>세금</div>
                          <div style={{ fontSize: '16px', fontWeight: '600', color: '#dc2626' }}>
                            -₩{Math.floor((viewingPaymentDetail.summary?.totalAmount || 0) * (getCounselorTaxRate(viewingPaymentDetail) / 100)).toLocaleString()}
                          </div>
                        </div>
                        <div>
                          <div style={{ fontSize: '12px', color: '#6b7280', marginBottom: '4px' }}>실 수령액</div>
                          <div style={{ fontSize: '18px', fontWeight: 'bold', color: '#059669' }}>
                            ₩{((viewingPaymentDetail.summary?.totalAmount || 0) - Math.floor((viewingPaymentDetail.summary?.totalAmount || 0) * (getCounselorTaxRate(viewingPaymentDetail) / 100))).toLocaleString()}
                          </div>
                        </div>
                      </div>
                    </div>

                    <div style={{ display: 'flex', gap: '8px', justifyContent: 'flex-end' }}>
                      <button 
                        onClick={() => handleDownloadPaymentReport(viewingPaymentDetail)}
                        style={{
                          padding: '8px 16px',
                          border: '1px solid #d1d5db',
                          borderRadius: '4px',
                          backgroundColor: 'white',
                          cursor: 'pointer',
                          fontSize: '14px'
                        }}
                      >
                        📄 정산서 다운로드
                      </button>
                      <button 
                        onClick={() => setViewingPaymentDetail(null)}
                        style={{
                          padding: '8px 16px',
                          border: 'none',
                          borderRadius: '4px',
                          backgroundColor: '#2563eb',
                          color: 'white',
                          cursor: 'pointer',
                          fontSize: '14px'
                        }}
                      >
                        닫기
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === 'settings' && (
            <div>
              <div style={cardStyle}>
                <h3 style={{ 
                  margin: '0 0 16px 0', 
                  fontSize: '18px', 
                  fontWeight: '500',
                  color: '#111827'
                }}>
                  플랫폼 설정
                </h3>
                <div style={{ display: 'grid', gap: '16px' }}>
                  <div>
                    <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', color: '#374151', marginBottom: '4px' }}>
                      플랫폼 이름
                    </label>
                    <input 
                      type="text" 
                      value="EAP Service Platform"
                      style={{ 
                        width: '100%', 
                        padding: '8px 12px', 
                        border: '1px solid #d1d5db', 
                        borderRadius: '6px',
                        fontSize: '14px'
                      }}
                    />
                  </div>
                  <div>
                    <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', color: '#374151', marginBottom: '4px' }}>
                      기본 상담 시간 (분)
                    </label>
                    <input 
                      type="number" 
                      value="50"
                      style={{ 
                        width: '100%', 
                        padding: '8px 12px', 
                        border: '1px solid #d1d5db', 
                        borderRadius: '6px',
                        fontSize: '14px'
                      }}
                    />
                  </div>
                  <div>
                    <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', color: '#374151', marginBottom: '4px' }}>
                      일일 최대 예약 수
                    </label>
                    <input 
                      type="number" 
                      value="10"
                      style={{ 
                        width: '100%', 
                        padding: '8px 12px', 
                        border: '1px solid #d1d5db', 
                        borderRadius: '6px',
                        fontSize: '14px'
                      }}
                    />
                  </div>
                  <button style={{
                    background: '#16a34a',
                    color: 'white',
                    border: 'none',
                    padding: '12px 24px',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontSize: '14px',
                    fontWeight: '500',
                    alignSelf: 'start'
                  }}>
                    설정 저장
                  </button>
                </div>
              </div>

              <div style={cardStyle}>
                <h3 style={{ 
                  margin: '0 0 16px 0', 
                  fontSize: '18px', 
                  fontWeight: '500',
                  color: '#111827'
                }}>
                  시스템 정보
                </h3>
                <div style={{ display: 'grid', gap: '8px', fontSize: '14px', color: '#6b7280' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <span>버전:</span>
                    <span style={{ color: '#111827' }}>v1.0.0</span>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <span>데이터베이스 상태:</span>
                    <span style={{ color: '#16a34a' }}>정상</span>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <span>총 사용자 수:</span>
                    <span style={{ color: '#111827' }}>{(platformStats?.totalEmployees || 0) + (platformStats?.totalCounselors || 0)}</span>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <span>총 상담 세션:</span>
                    <span style={{ color: '#111827' }}>{platformStats?.totalSessions || 0}</span>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default SuperAdminDashboard;
