        headers: { Authorization: `Bearer ${token}` }
      });
      
      setCounselingCenters(prev => prev.map(center =>
        center._id === editingCenter._id ? { ...center, ...centerForm } : center
      ));
      
      setEditingCenter(null);
      setCenterForm({});
