                            <button
                              onClick={() => toggleCompanyStatus(company._id)}
                              style={{
                                backgroundColor: company.status === 'active' ? '#f44336' : '#4caf50',
                                color: 'white',
                                border: 'none',
                                padding: '4px 8px',
                                borderRadius: '4px',
                                cursor: 'pointer',
                                fontSize: '11px'
                              }}
                            >
                              {company.status === 'active' ? '중단' : '활성'}
                            </button>
                            <button
                              style={{
                                backgroundColor: '#1976d2',
                                color: 'white',
                                border: 'none',
                                padding: '4px 8px',
                                borderRadius: '4px',
                                cursor: 'pointer',
                                fontSize: '11px'
                              }}
