                      onClick={() => setNewCompany(prev => ({ ...prev, plan: plan.key as any }))}
                      style={{
                        padding: '15px',
                        border: `2px solid ${newCompany.plan === plan.key ? '#1976d2' : '#e1e1e1'}`,
                        borderRadius: '8px',
                        cursor: 'pointer',
                        backgroundColor: newCompany.plan === plan.key ? '#e3f2fd' : 'white',
                        textAlign: 'center'
