import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MomentumTheme } from '../../styles/MomentumTheme.ts';

interface BookingModalProps {
  isOpen: boolean;
  onClose: () => void;
  sessionType: 'psychological' | 'financial';
  onSuccess?: () => void;
}

interface Counselor {
  _id: string;
  name: string;
  email: string;
  specialties?: string[];
  experience?: number;
  rating?: number;
  customRate?: number;
}

interface BookingData {
  counselorId: string;
  scheduledDate: string;
  duration: number;
  sessionType: string;
  format: 'face-to-face' | 'video-call' | 'phone-call';
  reason: string;
  notes: string;
}

const BookingModal: React.FC<BookingModalProps> = ({ isOpen, onClose, sessionType, onSuccess }) => {
  const [step, setStep] = useState(1);
  const [counselors, setCounselors] = useState<Counselor[]>([]);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [formData, setFormData] = useState<BookingData>({
    counselorId: '',
    scheduledDate: '',
    duration: 60,
    sessionType: sessionType === 'psychological' ? 'ê°œì¸ìƒë‹´' : 'goal-planning',
    format: 'video-call',
    reason: '',
    notes: ''
  });

  useEffect(() => {
    if (isOpen) {
      loadCounselors();
    }
  }, [isOpen, sessionType]);

  const loadCounselors = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const endpoint = sessionType === 'psychological' 
        ? '/api/users/counselors' 
        : '/api/users?role=financial-advisor';

      const response = await axios.get(`http://localhost:3000${endpoint}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCounselors(response.data);
    } catch (error) {
      console.error('ìƒë‹´ì‚¬ ëª©ë¡ ë¡œë“œ ì‹¤íŒ¨:', error);
      setCounselors([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async () => {
    try {
      setSubmitting(true);
      const token = localStorage.getItem('token');

      const endpoint = sessionType === 'psychological' 
        ? '/api/appointments'
        : '/api/financial-sessions';

      const submitData = sessionType === 'psychological' 
        ? {
            counselorId: formData.counselorId,
            scheduledDate: formData.scheduledDate,
            duration: formData.duration,
            type: 'individual',
            reason: formData.reason,
            notes: formData.notes
          }
        : {
            financialAdvisorId: formData.counselorId,
            scheduledDate: formData.scheduledDate,
            duration: formData.duration,
            sessionType: formData.sessionType,
            format: formData.format,
            preparation: {
              reason: formData.reason,
              notes: formData.notes
            }
          };

      await axios.post(`http://localhost:3000${endpoint}`, submitData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('ìƒë‹´ ì˜ˆì•½ì´ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤!');
      if (onSuccess) onSuccess();
      onClose();
      resetForm();
    } catch (error) {
      console.error('ì˜ˆì•½ ì‹¤íŒ¨:', error);
      console.error('ì˜¤ë¥˜ ìƒì„¸:', error.response?.data);

      let errorMessage = 'ì˜ˆì•½ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤. ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.';
      if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      } else if (error.response?.data?.errors) {
        errorMessage = error.response.data.errors.map(e => e.msg).join(', ');
      }

      alert(errorMessage);
    } finally {
      setSubmitting(false);
    }
  };

  const resetForm = () => {
    setStep(1);
    setFormData({
      counselorId: '',
      scheduledDate: '',
      duration: 60,
      sessionType: sessionType === 'psychological' ? 'ê°œì¸ìƒë‹´' : 'goal-planning',
      format: 'video-call',
      reason: '',
      notes: ''
    });
  };

  const handleClose = () => {
    onClose();
    resetForm();
  };

  if (!isOpen) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
