      <div style={{
        backgroundColor: 'white',
        borderRadius: '16px',
        width: '90%',
        maxWidth: '600px',
        maxHeight: '80vh',
        overflow: 'auto',
        position: 'relative'
      }}>
        {/* í—¤ë” */}
        <div style={{
          background: sessionType === 'psychological' 
            ? MomentumTheme.gradients.primary 
            : MomentumTheme.gradients.accent,
          color: 'white',
          padding: '24px',
          borderRadius: '16px 16px 0 0'
