              <button
                onClick={handleSubmitDispute}
                style={{
                  backgroundColor: '#f57c00',
                  color: 'white',
                  border: 'none',
                  padding: '10px 20px',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontWeight: 'bold'
                }}
              >
                이의제기 제출
              </button>
            </div>
          </div>
        </div>
