                    }
                    style={{
                      backgroundColor: 
                        (appointmentStep === 1 && (!appointmentForm.date || !appointmentForm.time)) ||
                        (appointmentStep === 2 && (!appointmentForm.type || !appointmentForm.topic))
                          ? '#ccc' : '#1976d2',
                      color: 'white',
                      border: 'none',
                      padding: '12px 20px',
                      borderRadius: '6px',
                      cursor: 
                        (appointmentStep === 1 && (!appointmentForm.date || !appointmentForm.time)) ||
                        (appointmentStep === 2 && (!appointmentForm.type || !appointmentForm.topic))
                          ? 'not-allowed' : 'pointer',
                      fontWeight: 'bold'
                    }}
                  >
                    다음 →
                  </button>
                ) : (
                  <button
                    onClick={createNewAppointment}
                    style={{
                      backgroundColor: '#1976d2',
                      color: 'white',
                      border: 'none',
                      padding: '12px 24px',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontWeight: 'bold'
                    }}
                  >
                    🎯 예약 완료
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
