import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface DashboardProps {
  user: any;
  onLogout: () => void;
}

const Dashboard: React.FC<DashboardProps> = ({ user, onLogout }) => {
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [appointments, setAppointments] = useState<any[]>([]);
  const [showNewAppointment, setShowNewAppointment] = useState(false);
  const [apiStatus, setApiStatus] = useState('testing');
  const [testResult, setTestResult] = useState('');
  const [appointmentStep, setAppointmentStep] = useState(1);
  const [appointmentForm, setAppointmentForm] = useState({
    date: '',
    time: '',
    type: '',
    topic: '',
    notes: '',
    urgency: 'normal'
  });

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');

      // API ÏÉÅÌÉú ÌÖåÏä§Ìä∏
      await testApiConnection();

      // ÎåÄÏãúÎ≥¥Îìú Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
      const response = await axios.get('http://localhost:3000/api/reports/dashboard', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStats(response.data);

      // ÏÇ¨Ïö©Ïûê ÏòàÏïΩ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ (counseling-sessions Ïö∞ÏÑ† ÏãúÎèÑ)
      try {
        const sessionsResponse = await axios.get('http://localhost:3000/api/counseling-sessions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        // ÏÉÅÎã¥ ÏÑ∏ÏÖò Îç∞Ïù¥ÌÑ∞Î•º ÏòàÏïΩ ÌòïÏãùÏúºÎ°ú Î≥ÄÌôò
        const sessions = sessionsResponse.data.sessions || sessionsResponse.data;
        const formattedAppointments = sessions.map((session: any) => ({
          _id: session._id,
          date: new Date(session.appointmentDate).toISOString().split('T')[0],
          time: new Date(session.appointmentDate).toTimeString().substr(0, 5),
          type: session.sessionType || 'individual',
          status: session.status,
          topic: session.topic,
          counselor: session.counselor?.name,
          assignmentStatus: session.assignmentStatus
        }));
        setAppointments(formattedAppointments);
      } catch (sessionError) {
        // ÏÉÅÎã¥ ÏÑ∏ÏÖò API Ïã§Ìå®Ïãú Í∏∞Ï°¥ appointments API ÏãúÎèÑ
        const appointmentsResponse = await axios.get('http://localhost:3000/api/appointments', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setAppointments(appointmentsResponse.data);
      }

    } catch (error) {
      console.error('Dashboard data fetch failed:', error);
      // Î∞±ÏóîÎìú Ïó∞Í≤∞ Ïã§Ìå®Ïãú Îç∞Î™® Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
      setStats({
        summary: {
          totalAppointments: 12,
          completedAppointments: 8,
          cancelledAppointments: 2,
          completionRate: 75
        }
      });
      setAppointments([
        { _id: '1', date: '2025-08-15', time: '14:00', type: 'individual', status: 'scheduled' },
        { _id: '2', date: '2025-08-20', time: '10:00', type: 'group', status: 'completed' }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const testApiConnection = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/health');
      if (response.ok) {
        const data = await response.json();
        setApiStatus('connected');
        setTestResult('‚úÖ Î∞±ÏóîÎìú API Ïó∞Í≤∞ ÏÑ±Í≥µ');
      }
    } catch (error) {
      setApiStatus('disconnected');
      setTestResult('‚ùå Î∞±ÏóîÎìú Ïó∞Í≤∞ Ïã§Ìå® - Îç∞Î™® Î™®ÎìúÎ°ú Ïã§Ìñâ');
    }
  };

  const openAppointmentModal = () => {
    setShowNewAppointment(true);
    setAppointmentStep(1);
    setAppointmentForm({
      date: '',
      time: '',
      type: '',
      topic: '',
      notes: '',
      urgency: 'normal'
    });
  };

  const closeAppointmentModal = () => {
    setShowNewAppointment(false);
    setAppointmentStep(1);
  };

  const nextStep = () => {
    if (appointmentStep < 3) {
      setAppointmentStep(appointmentStep + 1);
    }
  };

  const prevStep = () => {
    if (appointmentStep > 1) {
      setAppointmentStep(appointmentStep - 1);
    }
  };

  const updateAppointmentForm = (field: string, value: string) => {
    setAppointmentForm(prev => ({ ...prev, [field]: value }));
  };

  const generateTimeSlots = () => {
    const slots = [];
    for (let hour = 9; hour <= 17; hour++) {
      slots.push(`${hour.toString().padStart(2, '0')}:00`);
      if (hour < 17) slots.push(`${hour.toString().padStart(2, '0')}:30`);
    }
    return slots;
  };

  const generateAvailableDates = () => {
    const dates = [];
    const today = new Date();
    for (let i = 1; i <= 14; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      if (date.getDay() !== 0 && date.getDay() !== 6) { // Ï£ºÎßê Ï†úÏô∏
        dates.push({
          value: date.toISOString().split('T')[0],
          display: `${date.getMonth() + 1}/${date.getDate()} (${['Ïùº','Ïõî','Ìôî','Ïàò','Î™©','Í∏à','ÌÜ†'][date.getDay()]})`
        });
      }
    }
    return dates;
  };

  const createNewAppointment = async () => {
    try {
      const token = localStorage.getItem('token');

      // ÎÇ†ÏßúÏôÄ ÏãúÍ∞ÑÏùÑ Ìï©Ï≥êÏÑú appointmentDate ÏÉùÏÑ±
      const appointmentDateTime = new Date(`${appointmentForm.date}T${appointmentForm.time}:00`);

      const sessionData = {
        topic: appointmentForm.topic,
        counselingMethod: 'faceToFace', // Í∏∞Î≥∏Í∞í
        sessionType: appointmentForm.type,
        appointmentDate: appointmentDateTime.toISOString(),
        urgencyLevel: appointmentForm.urgency === 'urgent' ? 'high' : 
                     appointmentForm.urgency === 'normal' ? 'medium' : 'low',
        notes: appointmentForm.notes
      };

      const response = await axios.post('http://localhost:3000/api/counseling-sessions', sessionData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Î°úÏª¨ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ (UI ÌëúÏãúÏö©)
      const newAppointment = {
        _id: response.data.session._id,
        date: appointmentForm.date,
        time: appointmentForm.time,
        status: 'scheduled',
        topic: appointmentForm.topic,
        urgency: appointmentForm.urgency
      };

      setAppointments(prev => [...prev, newAppointment]);
      closeAppointmentModal();
      alert('ÏÉÅÎã¥ ÏöîÏ≤≠Ïù¥ Ï†ëÏàòÎêòÏóàÏäµÎãàÎã§! ÏÉÅÎã¥ÏÇ¨ Î∞∞Ï†ïÍπåÏßÄ Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.');

    } catch (error) {
      console.error('ÏÉÅÎã¥ ÏöîÏ≤≠ Ïã§Ìå®:', error);
      alert('ÏÉÅÎã¥ ÏöîÏ≤≠ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    onLogout();
  };

  const navigation = [
    { name: 'ÎåÄÏãúÎ≥¥Îìú', active: true },
    { name: 'ÏòàÏïΩ Í¥ÄÎ¶¨', active: false },
    { name: 'ÏÉÅÎã¥ Í∏∞Î°ù', active: false, roles: ['counselor', 'admin'] },
    { name: 'ÌåÄ Í¥ÄÎ¶¨', active: false, roles: ['manager', 'admin'], href: '/manager' },
    { name: 'ÏÇ¨Ïö©Ïûê Í¥ÄÎ¶¨', active: false, roles: ['admin'] },
    { name: 'Î¶¨Ìè¨Ìä∏', active: false, roles: ['admin', 'counselor'] },
    { name: 'Í∞àÎì± Ìï¥Í≤∞ Í∞ÄÏù¥Îìú', active: false }
  ].filter(item => !item.roles || item.roles.includes(user?.role || 'employee'));

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(to bottom, #f8fafc 0%, #e8f5e8 50%, #e3f2fd 100%)', 
      fontFamily: '"Segoe UI", "Malgun Gothic", sans-serif' 
    }}>
      {/* Header */}
      <header style={{
        background: 'linear-gradient(135deg, #4caf50 0%, #2196f3 100%)',
        color: 'white',
        padding: '0',
        boxShadow: '0 4px 20px rgba(76, 175, 80, 0.25)',
        position: 'relative',
        overflow: 'hidden'
      }}>
