import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface SuperAdminDashboardProps {
  user: any;
  onLogout: () => void;
}

interface Company {
  _id: string;
  name: string;
  domain: string;
  industry: string;
  employeeCount: number;
  activeCounselors: number;
  monthlyUsage: number;
  plan: 'basic' | 'premium' | 'enterprise';
  status: 'active' | 'inactive' | 'trial';
  createdAt: string;
  lastActivity: string;
  settings: {
    maxEmployees: number;
    maxCounselors: number;
    features: string[];
  };
}

interface Counselor {
  _id: string;
  name: string;
  email: string;
  phone: string;
  specialties: string[];
  licenseNumber: string;
  experience: number;
  rates: {
    faceToFace: number;
    phoneVideo: number;
    chat: number;
  };
  maxDailyAppointments: number;
  isActive: boolean;
  totalSessions: number;
  rating: number;
  createdAt: string;
}

interface PendingAssignment {
  _id: string;
  employee: {
    name: string;
    email: string;
    company: string;
    department: string;
  };
  topic: string;
  urgencyLevel: 'low' | 'medium' | 'high' | 'critical';
  counselingMethod: 'faceToFace' | 'phoneVideo' | 'chat';
  sessionType: string;
  requestedDate: string;
  assignmentStatus: 'pending' | 'assigned' | 'confirmed';
  createdAt: string;
}

interface CounselorPayment {
  _id: string;
  counselor: {
    _id: string;
    name: string;
  };
  year: number;
  month: number;
  summary: {
    totalSessions: number;
    faceToFaceSessions: number;
    phoneVideoSessions: number;
    chatSessions: number;
    totalAmount: number;
    taxAmount: number;
    netAmount: number;
  };
  status: 'pending' | 'approved' | 'paid' | 'dispute';
  approvedAt?: string;
  paidAt?: string;
}

interface PlatformStats {
  totalCompanies: number;
  totalEmployees: number;
  totalCounselors: number;
  totalSessions: number;
  activeCompanies: number;
  monthlyRevenue: number;
  growthRate: number;
}

interface CounselingCenter {
  _id: string;
  name: string;
  type: 'center' | 'independent';
  description: string;
  adminUser?: {
    name: string;
    email: string;
  };
  counselors: any[];
  businessLicense: string;
  address: {
    street: string;
    city: string;
    state: string;
    zipCode: string;
  };
  contact: {
    phone: string;
    email: string;
    website: string;
  };
  specialties: string[];
  isActive: boolean;
  totalSessions: number;
  rating: number;
  createdAt: string;
  activeCounselor: number;
  totalCounselors: number;
}

