import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface SuperAdminDashboardProps {
  user: any;
  onLogout: () => void;
}

interface PlatformStats {
  totalCompanies: number;
  totalEmployees: number;
  totalCounselors: number;
  totalSessions: number;
  activeCompanies: number;
  monthlyRevenue: number;
  growthRate: number;
}

const SuperAdminDashboard: React.FC<SuperAdminDashboardProps> = ({ user, onLogout }) => {
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [platformStats, setPlatformStats] = useState<PlatformStats | null>(null);
  const [companies, setCompanies] = useState<any[]>([]);
  const [counselors, setCounselors] = useState<any[]>([]);
  const [counselingCenters, setCounselingCenters] = useState<any[]>([]);
  const [pendingAssignments, setPendingAssignments] = useState<any[]>([]);
  const [payments, setPayments] = useState<any[]>([]);
  const [editingCounselor, setEditingCounselor] = useState<any>(null);
  const [editForm, setEditForm] = useState<any>({});
  const [editingAppointment, setEditingAppointment] = useState<any>(null);
  const [appointmentForm, setAppointmentForm] = useState<any>({});
  const [paymentPeriod, setPaymentPeriod] = useState<{startDate: string, endDate: string}>({
    startDate: '2024-11-01',
    endDate: '2024-11-30'
  });
  const [viewingPaymentDetail, setViewingPaymentDetail] = useState<any>(null);

  useEffect(() => {
    fetchSuperAdminData();
  }, []);

  const fetchSuperAdminData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // 병렬로 모든 데이터 조회
      const [statsResponse, companiesResponse, counselorsResponse, centersResponse, assignmentsResponse, paymentsResponse] = await Promise.all([
        axios.get('http://localhost:3000/api/super-admin/stats', { headers: { Authorization: `Bearer ${token}` } }),
        axios.get('http://localhost:3000/api/super-admin/companies', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: { companies: [] } })),
        axios.get('http://localhost:3000/api/counselors', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: { counselors: [] } })),
        axios.get('http://localhost:3000/api/counseling-centers', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: [] })),
        axios.get('http://localhost:3000/api/appointments', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: [] })),
        axios.get('http://localhost:3000/api/counselor-payments', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: { payments: [] } }))
      ]);

      setPlatformStats(statsResponse.data);
      setCompanies(companiesResponse.data.companies || []);
      setCounselors(counselorsResponse.data.counselors || []);
      setCounselingCenters(centersResponse.data || []);
      setPendingAssignments(assignmentsResponse.data || []);
      setPayments(paymentsResponse.data.payments || []);

    } catch (error) {
      console.error('Super admin data fetch failed:', error);
      // 데모 데이터로 폴백
      setPlatformStats({
        totalCompanies: 15,
        totalEmployees: 2450,
        totalCounselors: 45,
        totalSessions: 3280,
        activeCompanies: 13,
        monthlyRevenue: 125000,
        growthRate: 15.6
      });

      // 데모 데이터 설정
      setCompanies([
        { _id: '1', name: 'ABC기업', domain: 'abc.com', industry: 'IT', plan: 'premium', status: 'active', employeeCount: 150, createdAt: '2024-01-15' },
        { _id: '2', name: '대한회사', domain: 'daehan.co.kr', industry: '제조업', plan: 'basic', status: 'active', employeeCount: 85, createdAt: '2024-02-20' },
        { _id: '3', name: 'XYZ코퍼레이션', domain: 'xyz.com', industry: '금융', plan: 'enterprise', status: 'inactive', employeeCount: 300, createdAt: '2024-03-10' }
      ]);

      setCounselors([
        { _id: '1', name: '김상담사', email: 'kim@counselor.com', specialties: ['우울증', '불안장애'], isActive: true, experience: 5 },
        { _id: '2', name: '이심리사', email: 'lee@counselor.com', specialties: ['가족상담', '부부상담'], isActive: true, experience: 8 },
        { _id: '3', name: '박치료사', email: 'park@counselor.com', specialties: ['트라우마', '중독치료'], isActive: false, experience: 3 }
      ]);

      setPendingAssignments([
        { _id: '1', employee: { name: '홍직원', company: 'ABC기업', department: '개발팀' }, topic: '업무 스트레스', urgencyLevel: 'high', createdAt: '2024-12-01' },
        { _id: '2', employee: { name: '김사원', company: '대한회사', department: '영업팀' }, topic: '대인관계', urgencyLevel: 'medium', createdAt: '2024-12-02' }
      ]);

      setPayments([
        { _id: '1', counselor: { name: '김상담사' }, year: 2024, month: 11, summary: { totalSessions: 15, totalAmount: 750000 }, status: 'pending' },
        { _id: '2', counselor: { name: '이심리사' }, year: 2024, month: 11, summary: { totalSessions: 22, totalAmount: 1100000 }, status: 'approved' }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleEditCounselor = (counselor: any) => {
    setEditingCounselor(counselor);
    setEditForm({
      name: counselor.name,
      email: counselor.email,
      specialties: Array.isArray(counselor.specialties) ? counselor.specialties.join(', ') : counselor.specialties,
      experience: counselor.experience,
      isActive: counselor.isActive,
      counselingCenter: counselor.counselingCenter || '',
      isIndependent: counselor.isIndependent || false,
      taxRate: counselor.taxRate || 10.0
    });
  };

  const handleSaveCounselor = async () => {
    // 실제 API 호출은 나중에 구현
    console.log('상담사 정보 저장:', editForm);
    setEditingCounselor(null);
    setEditForm({});
    // 데이터 새로고침
    fetchSuperAdminData();
  };

  const handleCancelEdit = () => {
    setEditingCounselor(null);
    setEditForm({});
  };

  const handleToggleCounselorStatus = async (counselor: any) => {
    // 실제 API 호출은 나중에 구현
    console.log('상담사 상태 변경:', counselor.name, !counselor.isActive);
    // 데이터 새로고침
    fetchSuperAdminData();
  };

  // 배정관리 핸들러 함수들
  const handleCompleteAppointment = async (appointment: any) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:3000/api/appointments/${appointment._id}/complete`, 
        { status: 'completed' }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log('상담 완료 처리:', appointment._id);
      // 데이터 새로고침
      fetchSuperAdminData();
    } catch (error) {
      console.error('상담 완료 처리 실패:', error);
      // 임시로 로컬에서 상태 변경
      setPendingAssignments(prev => 
        prev.map(item => 
          item._id === appointment._id 
            ? { ...item, status: 'completed' }
            : item
        )
      );
    }
  };

  const handleEditAppointment = (appointment: any) => {
    setEditingAppointment(appointment);
    setAppointmentForm({
      scheduledDate: new Date(appointment.scheduledDate).toISOString().slice(0, 16),
      duration: appointment.duration,
      reason: appointment.reason,
      notes: appointment.notes || '',
      type: appointment.type,
      status: appointment.status,
      counselorId: appointment.counselor?._id || ''
    });
  };

  const handleSaveAppointment = async () => {
    try {
      const token = localStorage.getItem('token');

      // 빠른 배정 모드인 경우 상담사만 업데이트
      const updateData = appointmentForm.quickAssign ? 
        { counselorId: appointmentForm.counselorId } :
        {
          scheduledDate: new Date(appointmentForm.scheduledDate),
          duration: parseInt(appointmentForm.duration),
          reason: appointmentForm.reason,
          notes: appointmentForm.notes,
          type: appointmentForm.type,
          status: appointmentForm.status,
          counselorId: appointmentForm.counselorId
        };

      await axios.put(`http://localhost:3000/api/appointments/${editingAppointment._id}`, 
        updateData, 
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log(appointmentForm.quickAssign ? '상담사 배정 완료:' : '예약 정보 저장:', appointmentForm);
      setEditingAppointment(null);
      setAppointmentForm({});
      // 데이터 새로고침
      fetchSuperAdminData();
    } catch (error) {
      console.error('저장 실패:', error);
      // 임시로 로컬에서 업데이트
      setPendingAssignments(prev => 
        prev.map(item => {
          if (item._id === editingAppointment._id) {
            if (appointmentForm.quickAssign) {
              // 빠른 배정: 상담사만 업데이트
              const selectedCounselor = counselors.find(c => c._id === appointmentForm.counselorId);
              return { ...item, counselor: selectedCounselor };
            } else {
              // 전체 편집: 모든 필드 업데이트
              return { ...item, ...appointmentForm, scheduledDate: new Date(appointmentForm.scheduledDate) };
            }
          }
          return item;
        })
      );
      setEditingAppointment(null);
      setAppointmentForm({});
    }
  };

  const handleCancelAppointmentEdit = () => {
    setEditingAppointment(null);
    setAppointmentForm({});
  };

  const handleAssignCounselor = (appointment: any) => {
    // 상담사가 배정되지 않은 예약에 대해 빠른 배정 모달 열기
    setEditingAppointment(appointment);
    setAppointmentForm({
      counselorId: appointment.counselor?._id || '',
      quickAssign: true // 빠른 배정 모드 표시
    });
  };

  // 정산관리 핸들러 함수들
  const handleApprovePayment = async (paymentId: string) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:3000/api/counselor-payments/${paymentId}/approve`, 
        {}, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log('정산 승인:', paymentId);
      fetchSuperAdminData();
    } catch (error) {
      console.error('정산 승인 실패:', error);
      // 임시로 로컬에서 상태 변경
      setPayments(prev => 
        prev.map(payment => 
          payment._id === paymentId 
            ? { ...payment, status: 'processing' }
            : payment
        )
      );
    }
  };

  const handleRejectPayment = async (paymentId: string) => {
    const reason = prompt('정산 거절 사유를 입력하세요:');
    if (!reason) return;

    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:3000/api/counselor-payments/${paymentId}/status`, 
        { status: 'dispute', notes: `거절: ${reason}` }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log('정산 거절:', paymentId);
      fetchSuperAdminData();
    } catch (error) {
      console.error('정산 거절 실패:', error);
    }
  };

  const handleCompletePayment = async (paymentId: string) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:3000/api/counselor-payments/${paymentId}/pay`, 
        { paymentMethod: 'bank_transfer' }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log('지급 완료:', paymentId);
      fetchSuperAdminData();
    } catch (error) {
      console.error('지급 완료 실패:', error);
      // 임시로 로컬에서 상태 변경
      setPayments(prev => 
        prev.map(payment => 
          payment._id === paymentId 
            ? { ...payment, status: 'completed' }
            : payment
        )
      );
    }
  };

  const handleDownloadPaymentReport = (payment: any) => {
    // 정산서 다운로드 기능 구현
    const counselorName = payment.counselor?.name || '상담사';
    const isIndependent = counselorName.includes('개업') || counselorName === '박개업';
    const taxRate = isIndependent ? 3.3 : 10.0;
    const taxAmount = Math.floor((payment.summary?.totalAmount || 0) * (taxRate / 100));
    const netAmount = (payment.summary?.totalAmount || 0) - taxAmount;

    const reportContent = `
=== 정산서 ===
상담사: ${counselorName} (${isIndependent ? '개업상담사' : '센터소속'})
정산 기간: ${payment.year}년 ${payment.month}월
세율: ${taxRate}%

[상담 내역]
총 상담 횟수: ${payment.summary?.totalSessions || 0}회
- 대면 상담: ${payment.summary?.faceToFaceSessions || 0}회
- 화상/전화: ${payment.summary?.phoneVideoSessions || 0}회  
- 채팅 상담: ${payment.summary?.chatSessions || 0}회

[금액 계산]
총 상담료: ₩${(payment.summary?.totalAmount || 0).toLocaleString()}
세금 (${taxRate}%): -₩${taxAmount.toLocaleString()}
실 수령액: ₩${netAmount.toLocaleString()}

상태: ${payment.status === 'pending' ? '정산 대기' : 
       payment.status === 'processing' ? '정산 진행' : 
       payment.status === 'completed' ? '정산 완료' : '이의 신청'}

발행일: ${new Date().toLocaleDateString('ko-KR')}
    `.trim();

    // 텍스트 파일로 다운로드
    const blob = new Blob([reportContent], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `정산서_${counselorName}_${payment.year}년${payment.month}월.txt`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

