import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface DashboardProps {
  user: any;
  onLogout: () => void;
}

const Dashboard: React.FC<DashboardProps> = ({ user, onLogout }) => {
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [appointments, setAppointments] = useState<any[]>([]);
  const [showNewAppointment, setShowNewAppointment] = useState(false);
  const [apiStatus, setApiStatus] = useState('testing');
  const [testResult, setTestResult] = useState('');
  const [appointmentStep, setAppointmentStep] = useState(1);
  const [appointmentForm, setAppointmentForm] = useState({
    date: '',
    time: '',
    type: '',
    topic: '',
    notes: '',
    urgency: 'normal'
  });

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');

      // API ìƒíƒœ í…ŒìŠ¤íŠ¸
      await testApiConnection();

      // ëŒ€ì‹œë³´ë“œ ë°ì´í„° ë¶ˆëŸ¬ì˜¤ê¸°
      const response = await axios.get('http://localhost:3000/api/reports/dashboard', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStats(response.data);

      // ì‚¬ìš©ì ì˜ˆì•½ ëª©ë¡ ë¶ˆëŸ¬ì˜¤ê¸°
      const appointmentsResponse = await axios.get('http://localhost:3000/api/appointments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAppointments(appointmentsResponse.data);

    } catch (error) {
      console.error('Dashboard data fetch failed:', error);
      // ë°±ì—”ë“œ ì—°ê²° ì‹¤íŒ¨ì‹œ ë°ëª¨ ë°ì´í„° ì‚¬ìš©
      setStats({
        summary: {
          totalAppointments: 12,
          completedAppointments: 8,
          cancelledAppointments: 2,
          completionRate: 75
        }
      });
      setAppointments([
        { _id: '1', date: '2025-08-15', time: '14:00', type: 'individual', status: 'scheduled' },
        { _id: '2', date: '2025-08-20', time: '10:00', type: 'group', status: 'completed' }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const testApiConnection = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/health');
      if (response.ok) {
        const data = await response.json();
        setApiStatus('connected');
        setTestResult('âœ… ë°±ì—”ë“œ API ì—°ê²° ì„±ê³µ');
      }
    } catch (error) {
      setApiStatus('disconnected');
      setTestResult('âŒ ë°±ì—”ë“œ ì—°ê²° ì‹¤íŒ¨ - ë°ëª¨ ëª¨ë“œë¡œ ì‹¤í–‰');
    }
  };

  const openAppointmentModal = () => {
    setShowNewAppointment(true);
    setAppointmentStep(1);
    setAppointmentForm({
      date: '',
      time: '',
      type: '',
      topic: '',
      notes: '',
      urgency: 'normal'
    });
  };

  const closeAppointmentModal = () => {
    setShowNewAppointment(false);
    setAppointmentStep(1);
  };

  const nextStep = () => {
    if (appointmentStep < 3) {
      setAppointmentStep(appointmentStep + 1);
    }
  };

  const prevStep = () => {
    if (appointmentStep > 1) {
      setAppointmentStep(appointmentStep - 1);
    }
  };

  const updateAppointmentForm = (field: string, value: string) => {
    setAppointmentForm(prev => ({ ...prev, [field]: value }));
  };

  const generateTimeSlots = () => {
    const slots = [];
    for (let hour = 9; hour <= 17; hour++) {
      slots.push(`${hour.toString().padStart(2, '0')}:00`);
      if (hour < 17) slots.push(`${hour.toString().padStart(2, '0')}:30`);
    }
    return slots;
  };

  const generateAvailableDates = () => {
    const dates = [];
    const today = new Date();
    for (let i = 1; i <= 14; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      if (date.getDay() !== 0 && date.getDay() !== 6) { // ì£¼ë§ ì œì™¸
        dates.push({
          value: date.toISOString().split('T')[0],
          display: `${date.getMonth() + 1}/${date.getDate()} (${['ì¼','ì›”','í™”','ìˆ˜','ëª©','ê¸ˆ','í† '][date.getDay()]})`
        });
      }
    }
    return dates;
  };

  const createNewAppointment = async () => {
    try {
      const token = localStorage.getItem('token');

      const response = await axios.post('http://localhost:3000/api/appointments', appointmentForm, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setAppointments(prev => [...prev, response.data]);
      closeAppointmentModal();
      alert('ìƒˆ ì˜ˆì•½ì´ ì„±ê³µì ìœ¼ë¡œ ìƒì„±ë˜ì—ˆìŠµë‹ˆë‹¤!');

    } catch (error) {
      console.error('Appointment creation failed:', error);
      // ë°ëª¨ìš© ë¡œì»¬ ì¶”ê°€
      const demoAppointment = {
        _id: Date.now().toString(),
        ...appointmentForm,
        status: 'scheduled'
      };
      setAppointments(prev => [...prev, demoAppointment]);
      closeAppointmentModal();
      alert('ë°ëª¨ ì˜ˆì•½ì´ ì¶”ê°€ë˜ì—ˆìŠµë‹ˆë‹¤!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    onLogout();
  };

  const navigation = [
    { name: 'ëŒ€ì‹œë³´ë“œ', active: true },
    { name: 'ì˜ˆì•½ ê´€ë¦¬', active: false },
    { name: 'ìƒë‹´ ê¸°ë¡', active: false, roles: ['counselor', 'admin'] },
    { name: 'ì‚¬ìš©ì ê´€ë¦¬', active: false, roles: ['admin'] },
    { name: 'ë¦¬í¬íŠ¸', active: false, roles: ['admin', 'counselor'] },
    { name: 'ê°ˆë“± í•´ê²° ê°€ì´ë“œ', active: false }
  ].filter(item => !item.roles || item.roles.includes(user?.role || 'employee'));

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f5f5f5', fontFamily: 'Arial, sans-serif' }}>
      {/* Header */}
      <header style={{
        backgroundColor: 'white',
        borderBottom: '1px solid #e1e1e1',
        padding: '16px 0'
      }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 20px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <h1 style={{ color: '#333', fontSize: '24px', margin: '0' }}>
              ğŸ¢ EAP Service
            </h1>
            <p style={{ color: '#666', margin: '5px 0 0 0', fontSize: '14px' }}>
              ê·¼ë¡œìì§€ì›í”„ë¡œê·¸ë¨
            </p>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
            <span style={{ color: '#666', fontSize: '14px' }}>
              {user?.name}ë‹˜ ({user?.role === 'employee' ? 'ì§ì›' : user?.role === 'counselor' ? 'ìƒë‹´ì‚¬' : 'ê´€ë¦¬ì'})
            </span>
            <button
              onClick={handleLogout}
              style={{
                backgroundColor: '#f44336',
                color: 'white',
                border: 'none',
                padding: '8px 16px',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '14px'
              }}
            >
              ë¡œê·¸ì•„ì›ƒ
            </button>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav style={{
        backgroundColor: 'white',
        borderBottom: '1px solid #e1e1e1',
        padding: '0'
      }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 20px' }}>
          <div style={{ display: 'flex', gap: '0' }}>
            {navigation.map((item, index) => (
              <div
                key={index}
                style={{
                  padding: '12px 20px',
                  cursor: 'pointer',
                  borderBottom: item.active ? '3px solid #1976d2' : '3px solid transparent',
                  color: item.active ? '#1976d2' : '#666',
                  fontWeight: item.active ? 'bold' : 'normal',
                  fontSize: '14px'
                }}
              >
                {item.name}
              </div>
            ))}
          </div>
        </div>
      </nav>

      {/* Main Content */}
