sourcesContent:
import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface CounselorDashboardProps {
  user: any;
  onLogout: () => void;
}

interface Appointment {
  _id: string;
  date: string;
  time: string;
  type: 'individual' | 'group';
  topic: string;
  urgency: 'low' | 'normal' | 'high';
  status: 'scheduled' | 'in-progress' | 'completed' | 'cancelled';
  notes: string;
  client: {
    _id: string;
    name: string;
    email: string;
    department: string;
    position: string;
  };
  counselingRecord?: {
    summary: string;
    recommendations: string;
    followUpNeeded: boolean;
    nextSessionDate?: string;
  };
}

interface DailyStats {
  totalAppointments: number;
  completedToday: number;
  upcomingToday: number;
  highPriorityCount: number;
}

const CounselorDashboard: React.FC<CounselorDashboardProps> = ({ user, onLogout }) => {
  const [loading, setLoading] = useState(true);
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [dailyStats, setDailyStats] = useState<DailyStats | null>(null);
  const [apiStatus, setApiStatus] = useState('testing');
  const [testResult, setTestResult] = useState('');
  const [selectedAppointment, setSelectedAppointment] = useState<Appointment | null>(null);
  const [showRecordModal, setShowRecordModal] = useState(false);
  const [showClientHistory, setShowClientHistory] = useState(false);
  const [recordForm, setRecordForm] = useState({
    summary: '',
    recommendations: '',
    followUpNeeded: false,
    nextSessionDate: ''
  });
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterUrgency, setFilterUrgency] = useState('all');

  useEffect(() => {
    fetchCounselorData();
  }, []);

  const fetchCounselorData = async () => {
    try {
      // API 상태 테스트
      await testApiConnection();

      const token = localStorage.getItem('token');

      // 상담사 예약 목록 조회
      const appointmentsResponse = await axios.get('http://localhost:3000/api/counselor/appointments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAppointments(appointmentsResponse.data);

      // 오늘 통계 조회
      const statsResponse = await axios.get('http://localhost:3000/api/counselor/daily-stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDailyStats(statsResponse.data);

    } catch (error) {
      console.error('Counselor data fetch failed:', error);
      // 데모 데이터 사용
      const demoAppointments: Appointment[] = [
        {
          _id: '1',
          date: '2025-08-10',
          time: '09:00',
          type: 'individual',
          topic: '스트레스 관리',
          urgency: 'high',
          status: 'scheduled',
          notes: '업무 스트레스로 인한 불안감 호소',
          client: {
            _id: 'c1',
            name: '김직원',
            email: 'kim@company.com',
            department: 'IT개발팀',
            position: '선임연구원'
          }
        },
        {
          _id: '2',
          date: '2025-08-10',
          time: '10:30',
          type: 'individual',
          topic: '인간관계',
          urgency: 'normal',
          status: 'completed',
          notes: '동료와의 갈등 상황 상담',
          client: {
            _id: 'c2',
            name: '이사원',
            email: 'lee@company.com',
            department: 'IT개발팀',
            position: '주임연구원'
          },
          counselingRecord: {
            summary: '동료와의 의사소통 문제로 인한 갈등 상황. 상호 이해 부족이 주요 원인.',
            recommendations: '1. 적극적 경청 연습\n2. 감정 표현 방법 개선\n3. 정기적 팀 미팅 참여',
            followUpNeeded: true,
            nextSessionDate: '2025-08-24'
          }
        },
        {
          _id: '3',
          date: '2025-08-10',
          time: '14:00',
          type: 'group',
          topic: '워라밸',
          urgency: 'normal',
          status: 'scheduled',
          notes: '팀 전체 워라밸 개선 방안 논의',
          client: {
            _id: 'c3',
            name: '박대리',
            email: 'park@company.com',
            department: 'IT개발팀',
            position: '책임연구원'
          }
        },
        {
          _id: '4',
          date: '2025-08-10',
          time: '15:30',
          type: 'individual',
          topic: '진로 상담',
          urgency: 'low',
          status: 'scheduled',
          notes: '승진 관련 고민 상담',
          client: {
            _id: 'c4',
            name: '최과장',
            email: 'choi@company.com',
            department: '마케팅팀',
            position: '과장'
          }
        }
      ];

      setAppointments(demoAppointments);
      setDailyStats({
        totalAppointments: 4,
        completedToday: 1,
        upcomingToday: 3,
        highPriorityCount: 1
      });
    } finally {
      setLoading(false);
    }
  };

  const testApiConnection = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/health');
      if (response.ok) {
        setApiStatus('connected');
        setTestResult('✅ 백엔드 API 연결 성공');
      }
    } catch (error) {
      setApiStatus('disconnected');
      setTestResult('❌ 백엔드 연결 실패 - 데모 모드로 실행');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    onLogout();
  };

  const openRecordModal = (appointment: Appointment) => {
    setSelectedAppointment(appointment);
    if (appointment.counselingRecord) {
      setRecordForm({
        summary: appointment.counselingRecord.summary,
        recommendations: appointment.counselingRecord.recommendations,
        followUpNeeded: appointment.counselingRecord.followUpNeeded,
        nextSessionDate: appointment.counselingRecord.nextSessionDate || ''
      });
    } else {
      setRecordForm({
        summary: '',
        recommendations: '',
        followUpNeeded: false,
        nextSessionDate: ''
      });
    }
    setShowRecordModal(true);
  };

  const closeRecordModal = () => {
    setShowRecordModal(false);
    setSelectedAppointment(null);
    setRecordForm({
      summary: '',
      recommendations: '',
      followUpNeeded: false,
      nextSessionDate: ''
    });
  };

  const saveRecord = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:3000/api/counselor/appointments/${selectedAppointment?._id}/record`, recordForm, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // 로컬 상태 업데이트
      setAppointments(prev => prev.map(apt => 
        apt._id === selectedAppointment?._id 
          ? { ...apt, counselingRecord: recordForm, status: 'completed' as const }
          : apt
      ));

      closeRecordModal();
      alert('상담 기록이 저장되었습니다!');
    } catch (error) {
      // 데모 모드에서도 동작
      setAppointments(prev => prev.map(apt => 
        apt._id === selectedAppointment?._id 
          ? { ...apt, counselingRecord: recordForm, status: 'completed' as const }
          : apt
      ));
      closeRecordModal();
      alert('상담 기록이 저장되었습니다! (데모 모드)');
    }
  };

  const updateAppointmentStatus = async (appointmentId: string, newStatus: string) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:3000/api/counselor/appointments/${appointmentId}/status`, 
        { status: newStatus }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
    } catch (error) {
      console.log('Status update failed, using demo mode');
    }

    setAppointments(prev => prev.map(apt => 
      apt._id === appointmentId ? { ...apt, status: newStatus as any } : apt
    ));
  };

  const getUrgencyColor = (urgency: string) => {
    switch (urgency) {
