import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface SuperAdminDashboardProps {
  user: any;
  onLogout: () => void;
}

interface PlatformStats {
  totalCompanies: number;
  totalEmployees: number;
  totalCounselors: number;
  totalSessions: number;
  activeCompanies: number;
  monthlyRevenue: number;
  growthRate: number;
}

const SuperAdminDashboard: React.FC<SuperAdminDashboardProps> = ({ user, onLogout }) => {
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [platformStats, setPlatformStats] = useState<PlatformStats | null>(null);
  const [companies, setCompanies] = useState<any[]>([]);
  const [counselors, setCounselors] = useState<any[]>([]);
  const [pendingAssignments, setPendingAssignments] = useState<any[]>([]);
  const [payments, setPayments] = useState<any[]>([]);
  const [editingCounselor, setEditingCounselor] = useState<any>(null);
  const [editForm, setEditForm] = useState<any>({});
  const [editingAppointment, setEditingAppointment] = useState<any>(null);
  const [appointmentForm, setAppointmentForm] = useState<any>({});

  useEffect(() => {
    fetchSuperAdminData();
  }, []);

  const fetchSuperAdminData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // 병렬로 모든 데이터 조회
      const [statsResponse, companiesResponse, counselorsResponse, assignmentsResponse, paymentsResponse] = await Promise.all([
        axios.get('http://localhost:3000/api/super-admin/stats', { headers: { Authorization: `Bearer ${token}` } }),
        axios.get('http://localhost:3000/api/super-admin/companies', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: { companies: [] } })),
        axios.get('http://localhost:3000/api/counselors', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: { counselors: [] } })),
        axios.get('http://localhost:3000/api/appointments', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: [] })),
        axios.get('http://localhost:3000/api/counselor-payments', { headers: { Authorization: `Bearer ${token}` } }).catch(() => ({ data: { payments: [] } }))
      ]);

      setPlatformStats(statsResponse.data);
      setCompanies(companiesResponse.data.companies || []);
      setCounselors(counselorsResponse.data.counselors || []);
      setPendingAssignments(assignmentsResponse.data || []);
      setPayments(paymentsResponse.data.payments || []);

    } catch (error) {
      console.error('Super admin data fetch failed:', error);
      // 데모 데이터로 폴백
      setPlatformStats({
        totalCompanies: 15,
        totalEmployees: 2450,
        totalCounselors: 45,
        totalSessions: 3280,
        activeCompanies: 13,
        monthlyRevenue: 125000,
        growthRate: 15.6
      });

      // 데모 데이터 설정
      setCompanies([
        { _id: '1', name: 'ABC기업', domain: 'abc.com', industry: 'IT', plan: 'premium', status: 'active', employeeCount: 150, createdAt: '2024-01-15' },
        { _id: '2', name: '대한회사', domain: 'daehan.co.kr', industry: '제조업', plan: 'basic', status: 'active', employeeCount: 85, createdAt: '2024-02-20' },
        { _id: '3', name: 'XYZ코퍼레이션', domain: 'xyz.com', industry: '금융', plan: 'enterprise', status: 'inactive', employeeCount: 300, createdAt: '2024-03-10' }
      ]);

      setCounselors([
        { _id: '1', name: '김상담사', email: 'kim@counselor.com', specialties: ['우울증', '불안장애'], isActive: true, experience: 5 },
        { _id: '2', name: '이심리사', email: 'lee@counselor.com', specialties: ['가족상담', '부부상담'], isActive: true, experience: 8 },
        { _id: '3', name: '박치료사', email: 'park@counselor.com', specialties: ['트라우마', '중독치료'], isActive: false, experience: 3 }
      ]);

      setPendingAssignments([
