import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import Login from './pages/Login.tsx';
import Dashboard from './pages/Dashboard.tsx';

function App() {
  const [user, setUser] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [showDemo, setShowDemo] = useState(false);
  const [testResult, setTestResult] = useState<string>('');

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const response = await axios.get('http://localhost:5001/api/users/profile', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUser(response.data);
      } catch (error) {
        localStorage.removeItem('token');
      }
    }
    setLoading(false);
  };

  const handleLogin = (userData: any) => {
    setUser(userData);
  };

  const handleLogout = () => {
    setUser(null);
  };

  const testAPI = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/health');
      const data = await response.json();
      setTestResult(JSON.stringify(data, null, 2));
    } catch (error) {
      setTestResult('Error: ' + error);
    }
  };

  const testRegister = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: 'test@example.com',
          password: '123456',
          name: '테스트사용자',
          role: 'employee',
          department: 'IT'
        })
      });
      const data = await response.json();
      setTestResult(JSON.stringify(data, null, 2));
    } catch (error) {
      setTestResult('Error: ' + error);
    }
  };

  if (loading) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        backgroundColor: '#f5f5f5'
      }}>
        <div style={{ textAlign: 'center' }}>
          <h2 style={{ color: '#333' }}>EAP Service 로딩 중...</h2>
          <div style={{ 
            width: '50px', 
            height: '50px', 
            border: '3px solid #f3f3f3',
            borderTop: '3px solid #1976d2',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '20px auto'
          }}></div>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/login"
            element={
              user ? <Navigate to="/dashboard" replace /> : <Login onLogin={handleLogin} />
            }
          />
          <Route
            path="/dashboard"
            element={
              user ? <Dashboard user={user} onLogout={handleLogout} /> : <Navigate to="/login" replace />
            }
          />
          <Route
            path="/demo"
            element={
              <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '800px', margin: '0 auto' }}>
                <header style={{ backgroundColor: '#f5f5f5', padding: '20px', marginBottom: '20px', borderRadius: '8px' }}>
                  <h1 style={{ color: '#333', margin: '0' }}>🏢 EAP Service - 데모 모드</h1>
                  <p style={{ color: '#666', margin: '10px 0 0 0' }}>개발 완료된 기능들을 확인해보세요</p>
                  <div style={{ marginTop: '15px' }}>
                    <button
                      onClick={() => window.location.href = '/login'}
                      style={{
                        backgroundColor: '#1976d2',
                        color: 'white',
                        border: 'none',
                        padding: '10px 20px',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        marginRight: '10px'
                      }}
                    >
                      🔐 로그인 페이지로
                    </button>
                  </div>
                </header>

                <div style={{ backgroundColor: '#e8f5e8', padding: '20px', borderRadius: '8px', marginBottom: '20px' }}>
                  <h2>✅ 완성된 UI 페이지들</h2>
                  <ul>
                    <li><strong>로그인 페이지</strong> - 실제 작동하는 로그인 폼</li>
                    <li><strong>대시보드</strong> - 권한별 네비게이션과 통계</li>
                    <li><strong>반응형 디자인</strong> - 모바일/태블릿 지원</li>
                  </ul>
                </div>

                <div style={{ backgroundColor: '#d1ecf1', padding: '20px', borderRadius: '8px', marginBottom: '20px' }}>
                  <h2>🧪 API 테스트</h2>
                  <div style={{ marginBottom: '10px' }}>
                    <button 
                      onClick={testAPI}
                      style={{
                        backgroundColor: '#28a745',
                        color: 'white',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        marginRight: '10px'
                      }}
                    >
                      API 상태 확인
                    </button>

                    <button 
                      onClick={testRegister}
                      style={{
                        backgroundColor: '#007bff',
                        color: 'white',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer'
                      }}
                    >
                      사용자 등록 테스트
                    </button>
                  </div>

                  {testResult && (
                    <div style={{ 
                      backgroundColor: '#f8f9fa', 
                      padding: '15px', 
                      borderRadius: '5px', 
                      border: '1px solid #dee2e6',
                      marginTop: '15px'
                    }}>
                      <h4>응답 결과:</h4>
                      <pre style={{ margin: '0', fontSize: '14px' }}>{testResult}</pre>
                    </div>
                  )}
                </div>

                <div style={{ backgroundColor: '#fff3cd', padding: '20px', borderRadius: '8px' }}>
                  <h2>🚀 백엔드 API 기능</h2>
                  <ul>
                    <li>✅ 사용자 인증/권한 관리</li>
                    <li>✅ 상담 예약 시스템</li>
                    <li>✅ 상담 기록 관리</li>
                    <li>✅ 관리자 대시보드</li>
                    <li>✅ 리포트 생성 및 내보내기</li>
                    <li>✅ MongoDB 스키마 완성</li>
                  </ul>
                </div>
              </div>
            }
          />
          <Route path="/" element={<Navigate to="/demo" replace />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
