import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface CompanyAdminDashboardProps {
  user: any;
  onLogout: () => void;
}

interface Employee {
  _id: string;
  name: string;
  email: string;
  department: string;
  position: string;
  role: 'employee' | 'manager';
  annualLimit: number;
  usedSessions: number;
  joinDate: string;
  isActive: boolean;
}


interface CompanyStats {
  totalEmployees: number;
  totalSessions: number;
  completedSessions: number;
  monthlyUsage: number;
  departmentUsage: { [key: string]: number };
  utilizationRate: number;
}

const CompanyAdminDashboard: React.FC<CompanyAdminDashboardProps> = ({ user, onLogout }) => {
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [companyStats, setCompanyStats] = useState<CompanyStats | null>(null);

  // Î∂ÄÏÑú Í¥ÄÎ¶¨
  const [departments, setDepartments] = useState<string[]>([]);
  const [showDepartmentModal, setShowDepartmentModal] = useState(false);
  const [newDepartment, setNewDepartment] = useState('');

  // ÏÇ¨Ïö©Ïûê Îì±Î°ù Ìèº
  const [showAddUser, setShowAddUser] = useState(false);
  const [newUser, setNewUser] = useState({
    name: '',
    email: '',
    department: '',
    position: '',
    role: 'employee' as 'employee' | 'manager',
    annualLimit: 12
  });

  // Î¶¨Ìè¨Ìä∏ ÏÉùÏÑ± Ìèº
  const [selectedMonthlyPeriod, setSelectedMonthlyPeriod] = useState('2025ÎÖÑ 1Ïõî');
  const [selectedSatisfactionPeriod, setSelectedSatisfactionPeriod] = useState('Î∂ÑÍ∏∞Î≥Ñ');
  const [selectedCostYear, setSelectedCostYear] = useState('2024ÎÖÑ');

  // Î¶¨Ìè¨Ìä∏ Í≤∞Í≥º Î™®Îã¨
  const [showReportModal, setShowReportModal] = useState(false);
  const [currentReport, setCurrentReport] = useState<any>(null);
  const [reportType, setReportType] = useState<'monthly' | 'satisfaction' | 'cost'>('monthly');

  // ÏóëÏÖÄ ÏóÖÎ°úÎìú Í¥ÄÎ†®
  const [showExcelUpload, setShowExcelUpload] = useState(false);
  const [uploading, setUploading] = useState(false);

  // ÌöåÏÇ¨ ÎπÑÏ¶àÎãàÏä§ Î©îÌä∏Î¶≠ ÏÑ§Ï†ï
  const [businessMetrics, setBusinessMetrics] = useState({
    annualRevenue: 0,
    avgEmployeeSalary: 50000000,
    preEapAbsenteeismDays: 0,
    preEapTurnoverRate: 15,
    dailyProductivityPerEmployee: 200000,
    recruitmentTrainingCost: 10000000
  });


  useEffect(() => {
    fetchCompanyData();
    fetchDepartments();
    fetchBusinessMetrics();
  }, []);

  const fetchCompanyData = async () => {
    try {

      const token = localStorage.getItem('token');

      // ÌöåÏÇ¨ Ï†ïÎ≥¥ Ï°∞Ìöå
      try {
        const companyResponse = await axios.get('http://localhost:3000/api/companies/my-company', {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log('ÌöåÏÇ¨ Ï†ïÎ≥¥:', companyResponse.data.company);
      } catch (error) {
        console.log('ÌöåÏÇ¨ Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå® (Îç∞Î™® Î™®Îìú):', error.response?.data?.error);
      }

      // ÌöåÏÇ¨ ÌÜµÍ≥Ñ Ï°∞Ìöå
      const statsResponse = await axios.get('http://localhost:3000/api/company-admin/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCompanyStats(statsResponse.data);

      // ÏßÅÏõê Î™©Î°ù Ï°∞Ìöå
      const employeesResponse = await axios.get('http://localhost:3000/api/company-admin/employees', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEmployees(employeesResponse.data);


    } catch (error) {
      console.error('Company admin data fetch failed:', error);
      // Îç∞Î™® Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
      setCompanyStats({
        totalEmployees: 45,
        totalSessions: 120,
        completedSessions: 98,
        monthlyUsage: 35,
        departmentUsage: {
          'ITÍ∞úÎ∞úÌåÄ': 15,
          'ÎßàÏºÄÌåÖÌåÄ': 12,
          'ÏòÅÏóÖÌåÄ': 8,
          'Ïù∏ÏÇ¨ÌåÄ': 5,
          'Ïû¨Î¨¥ÌåÄ': 3
        },
        utilizationRate: 72
      });

      setEmployees([
        {
          _id: '1',
          name: 'ÍπÄÏßÅÏõê',
          email: 'kim@company.com',
          department: 'ITÍ∞úÎ∞úÌåÄ',
          position: 'ÏÑ†ÏûÑÏó∞Íµ¨Ïõê',
          role: 'employee',
          annualLimit: 12,
          usedSessions: 3,
          joinDate: '2024-01-15',
          isActive: true
        },
        {
          _id: '2',
          name: 'Ïù¥Î∂ÄÏû•',
          email: 'lee@company.com',
          department: 'ITÍ∞úÎ∞úÌåÄ',
          position: 'Î∂ÄÏû•',
          role: 'manager',
          annualLimit: 15,
          usedSessions: 5,
          joinDate: '2023-05-20',
          isActive: true
        },
        {
          _id: '3',
          name: 'Î∞ïÎåÄÎ¶¨',
          email: 'park@company.com',
          department: 'ÎßàÏºÄÌåÖÌåÄ',
          position: 'ÎåÄÎ¶¨',
          role: 'employee',
          annualLimit: 12,
          usedSessions: 2,
          joinDate: '2024-03-10',
          isActive: true
        }
      ]);

    } finally {
      setLoading(false);
    }
  };

  const fetchDepartments = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/company-admin/departments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDepartments(response.data.departments);
    } catch (error) {
      console.error('Î∂ÄÏÑú Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
      // Í∏∞Î≥∏ Î∂ÄÏÑú Î™©Î°ù ÏÇ¨Ïö©
      setDepartments(['Í≤ΩÏòÅÏßÑ', 'ITÍ∞úÎ∞úÌåÄ', 'ÎßàÏºÄÌåÖÌåÄ', 'ÏòÅÏóÖÌåÄ', 'Ïù∏ÏÇ¨ÌåÄ', 'Ïû¨Î¨¥ÌåÄ', 'Ï¥ùÎ¨¥ÌåÄ', 'Í∏∞ÌöçÌåÄ', 'ÎîîÏûêÏù∏ÌåÄ', 'ÌíàÏßàÍ¥ÄÎ¶¨ÌåÄ']);
    }
  };

  const updateDepartments = async (updatedDepartments: string[]) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:3000/api/company-admin/departments', 
        { departments: updatedDepartments },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setDepartments(response.data.departments);
      alert('Î∂ÄÏÑú Î™©Î°ùÏù¥ ÏóÖÎç∞Ïù¥Ìä∏ÎêòÏóàÏäµÎãàÎã§.');
    } catch (error) {
      console.error('Î∂ÄÏÑú Î™©Î°ù ÏóÖÎç∞Ïù¥Ìä∏ Ïã§Ìå®:', error);
      alert('Î∂ÄÏÑú Î™©Î°ù ÏóÖÎç∞Ïù¥Ìä∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const addDepartment = () => {
    if (!newDepartment.trim()) return;
    if (departments.includes(newDepartment.trim())) {
      alert('Ïù¥ÎØ∏ Ï°¥Ïû¨ÌïòÎäî Î∂ÄÏÑúÎ™ÖÏûÖÎãàÎã§.');
      return;
    }
    const updatedDepartments = [...departments, newDepartment.trim()];
    updateDepartments(updatedDepartments);
    setNewDepartment('');
  };

  const removeDepartment = (deptToRemove: string) => {
    if (window.confirm(`"${deptToRemove}" Î∂ÄÏÑúÎ•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?`)) {
      const updatedDepartments = departments.filter(dept => dept !== deptToRemove);
      updateDepartments(updatedDepartments);
    }
  };

  const fetchBusinessMetrics = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/company-admin/business-metrics', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setBusinessMetrics(response.data.businessMetrics);
    } catch (error) {
      console.error('ÎπÑÏ¶àÎãàÏä§ Î©îÌä∏Î¶≠ Ï°∞Ìöå Ïã§Ìå®:', error);
      // Í∏∞Î≥∏Í∞í ÏÇ¨Ïö©
    }
  };

  const updateBusinessMetrics = async (metrics: any) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put('http://localhost:3000/api/company-admin/business-metrics', 
        { businessMetrics: metrics },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setBusinessMetrics(metrics);
      alert('ÌöåÏÇ¨ ÏÑ§Ï†ïÏù¥ Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§.');
    } catch (error) {
      console.error('ÎπÑÏ¶àÎãàÏä§ Î©îÌä∏Î¶≠ Ï†ÄÏû• Ïã§Ìå®:', error);
      alert('ÏÑ§Ï†ï Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };


  const handleLogout = () => {
    localStorage.removeItem('token');
    onLogout();
  };

  const addEmployee = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:3000/api/company-admin/employees', newUser, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setEmployees(prev => [...prev, response.data]);
      setShowAddUser(false);
      resetNewUser();
      alert('ÏßÅÏõêÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Îì±Î°ùÎêòÏóàÏäµÎãàÎã§!');
    } catch (error) {
      // Îç∞Î™® Î™®Îìú
      const demoEmployee: Employee = {
        _id: Date.now().toString(),
        ...newUser,
        usedSessions: 0,
        joinDate: new Date().toISOString().split('T')[0],
        isActive: true
      };

      setEmployees(prev => [...prev, demoEmployee]);
      setShowAddUser(false);
      resetNewUser();
      alert('ÏßÅÏõêÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Îì±Î°ùÎêòÏóàÏäµÎãàÎã§! (Îç∞Î™® Î™®Îìú)');
    }
  };


  const resetNewUser = () => {
    setNewUser({
      name: '',
      email: '',
      department: '',
      position: '',
      role: 'employee',
      annualLimit: 12
    });
  };

  // ÏóëÏÖÄ ÏÉòÌîå ÌååÏùº Îã§Ïö¥Î°úÎìú
  const downloadSampleExcel = () => {
    const csvContent = `Ïù¥Î¶Ñ,Ïù¥Î©îÏùº,Î∂ÄÏÑú,ÏßÅÏ±Ö,Ïó≠Ìï†,Ïó∞Í∞ÑÏÉÅÎã¥ÌïúÎèÑ
ÍπÄÏßÅÏõê,kim@company.com,Í∞úÎ∞úÌåÄ,ÏÑ†ÏûÑÍ∞úÎ∞úÏûê,employee,12
Ïù¥ÏßÅÏõê,lee@company.com,ÎßàÏºÄÌåÖÌåÄ,ÌåÄÏû•,manager,15
Î∞ïÏßÅÏõê,park@company.com,Ïù∏ÏÇ¨ÌåÄ,ÎåÄÎ¶¨,employee,10`;

    const BOM = '\uFEFF'; // UTF-8 BOM for proper Korean display in Excel
    const blob = new Blob([BOM + csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);

    link.setAttribute('href', url);
    link.setAttribute('download', 'ÏßÅÏõêÎì±Î°ù_ÏÉòÌîåÌååÏùº.csv');
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // ÏóëÏÖÄ ÌååÏùº ÏóÖÎ°úÎìú Ï≤òÎ¶¨
  const handleExcelUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (!file.name.endsWith('.csv') && !file.name.endsWith('.xlsx')) {
      alert('CSV ÎòêÎäî Excel ÌååÏùº(.xlsx)Îßå ÏóÖÎ°úÎìú Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }

    setUploading(true);

    try {
      const text = await file.text();
      const lines = text.split('\n').filter(line => line.trim());

      if (lines.length <= 1) {
        alert('ÌååÏùºÏóê Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.');
        setUploading(false);
        return;
      }

      // Ìó§Îçî Í≤ÄÏ¶ù
      const header = lines[0].replace(/\uFEFF/g, ''); // BOM Ï†úÍ±∞
      const expectedHeaders = ['Ïù¥Î¶Ñ', 'Ïù¥Î©îÏùº', 'Î∂ÄÏÑú', 'ÏßÅÏ±Ö', 'Ïó≠Ìï†', 'Ïó∞Í∞ÑÏÉÅÎã¥ÌïúÎèÑ'];
      const actualHeaders = header.split(',').map(h => h.trim());

      const hasValidHeaders = expectedHeaders.every(expected => 
        actualHeaders.some(actual => actual.includes(expected) || expected.includes(actual))
      );

      if (!hasValidHeaders) {
        alert(`Ïò¨Î∞îÎ•∏ Ìó§Îçî ÌòïÏãùÏù¥ ÏïÑÎãôÎãàÎã§.\nÌïÑÏöîÌïú Ïª¨Îüº: ${expectedHeaders.join(', ')}\nÌòÑÏû¨ Ïª¨Îüº: ${actualHeaders.join(', ')}`);
        setUploading(false);
        return;
      }

      // Îç∞Ïù¥ÌÑ∞ ÌååÏã±
      const newEmployees: any[] = [];
      const errors: string[] = [];

      for (let i = 1; i < lines.length; i++) {
        const line = lines[i].trim();
        if (!line) continue;

        const values = line.split(',').map(v => v.trim());

        if (values.length < 6) {
          errors.push(`${i + 1}Ìñâ: ÌïÑÏàò Ïª¨ÎüºÏù¥ Î∂ÄÏ°±Ìï©ÎãàÎã§.`);
          continue;
        }

        const [name, email, department, position, role, annualLimitStr] = values;

        // Ïú†Ìö®ÏÑ± Í≤ÄÏ¶ù
        if (!name || !email || !department || !position) {
          errors.push(`${i + 1}Ìñâ: ÌïÑÏàò Ï†ïÎ≥¥Í∞Ä ÎàÑÎùΩÎêòÏóàÏäµÎãàÎã§.`);
          continue;
        }

        if (!email.includes('@')) {
          errors.push(`${i + 1}Ìñâ: Ïò¨Î∞îÎ•∏ Ïù¥Î©îÏùº ÌòïÏãùÏù¥ ÏïÑÎãôÎãàÎã§.`);
          continue;
        }

        const annualLimit = parseInt(annualLimitStr) || 12;
        const employeeRole = role.toLowerCase() === 'manager' ? 'manager' : 'employee';

        newEmployees.push({
          _id: `excel_${Date.now()}_${i}`,
          name,
          email,
          department,
          position,
          role: employeeRole,
          annualLimit,
          usedSessions: 0,
          joinDate: new Date().toISOString().split('T')[0],
          isActive: true
        });
      }

      if (errors.length > 0) {
        alert(`ÏóÖÎ°úÎìú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§:\n${errors.slice(0, 10).join('\n')}${errors.length > 10 ? '\n...' : ''}`);
      }

      if (newEmployees.length > 0) {
        setEmployees(prev => [...prev, ...newEmployees]);
        alert(`${newEmployees.length}Î™ÖÏùò ÏßÅÏõêÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Îì±Î°ùÎêòÏóàÏäµÎãàÎã§!`);
        setShowExcelUpload(false);
      }

    } catch (error) {
      console.error('Excel upload error:', error);
      alert('ÌååÏùº Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. ÌååÏùº ÌòïÏãùÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
    } finally {
      setUploading(false);
      // ÌååÏùº input Î¶¨ÏÖã
      if (event.target) {
        event.target.value = '';
      }
    }
  };


  const toggleEmployeeStatus = (employeeId: string) => {
    setEmployees(prev => prev.map(emp => 
      emp._id === employeeId ? { ...emp, isActive: !emp.isActive } : emp
    ));
  };

  // Î¶¨Ìè¨Ìä∏ ÏÉùÏÑ± Ìï®ÏàòÎì§
  const generateMonthlyReport = async (selectedPeriod: string) => {
    try {
      const token = localStorage.getItem('token');
      const [year, month] = selectedPeriod.replace('ÎÖÑ ', '/').replace('Ïõî', '').split('/');
      const response = await axios.post('http://localhost:3000/api/company-admin/reports/monthly', {
        year: parseInt(year),
        month: parseInt(month)
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCurrentReport(response.data.report);
      setReportType('monthly');
      setShowReportModal(true);
    } catch (error) {
      console.error('ÏõîÍ∞Ñ Î¶¨Ìè¨Ìä∏ ÏÉùÏÑ± Ïã§Ìå®:', error);
      // Îç∞Î™® Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
      const demoReport = {
        period: selectedPeriod,
        totalSessions: 45,
        completedSessions: 38,
        departmentStats: {
          'ITÍ∞úÎ∞úÌåÄ': { totalSessions: 15, completedSessions: 13, employeeCount: 8 },
          'ÎßàÏºÄÌåÖÌåÄ': { totalSessions: 12, completedSessions: 10, employeeCount: 6 },
          'ÏòÅÏóÖÌåÄ': { totalSessions: 10, completedSessions: 8, employeeCount: 5 },
          'Ïù∏ÏÇ¨ÌåÄ': { totalSessions: 5, completedSessions: 4, employeeCount: 3 },
          'Ïû¨Î¨¥ÌåÄ': { totalSessions: 3, completedSessions: 3, employeeCount: 2 }
        },
        generatedAt: new Date(),
        reportType: 'monthly'
      };
      setCurrentReport(demoReport);
      setReportType('monthly');
      setShowReportModal(true);
    }
  };

  const generateSatisfactionReport = async (period: string) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:3000/api/company-admin/reports/satisfaction', {
        period
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCurrentReport(response.data.report);
      setReportType('satisfaction');
      setShowReportModal(true);
    } catch (error) {
      console.error('ÎßåÏ°±ÎèÑ Î¶¨Ìè¨Ìä∏ ÏÉùÏÑ± Ïã§Ìå®:', error);
      // Îç∞Î™® Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
      const demoReport = {
        period,
        averageRating: 4.2,
        responseRate: 75,
        totalResponses: 89,
        categoryRatings: {
          'ÏÉÅÎã¥ÏÇ¨ Ï†ÑÎ¨∏ÏÑ±': 4.5,
          'ÏÉÅÎã¥ ÌôòÍ≤Ω': 4.1,
          'ÏòàÏïΩ Ìé∏ÏùòÏÑ±': 3.9,
          'ÎπÑÎ∞ÄÎ≥¥Ïû•': 4.6,
          'Ï†ÑÎ∞òÏ†Å ÎßåÏ°±ÎèÑ': 4.2
        },
        improvements: [
          'ÏòàÏïΩ ÏãúÏä§ÌÖú Í∞úÏÑ† ÏöîÏ≤≠',
          'ÏÉÅÎã¥Ïã§ ÌôòÍ≤Ω Í∞úÏÑ†',
          'Îã§ÏñëÌïú ÏÉÅÎã¥ Î∞©Ïãù Ï†úÍ≥µ',
          'ÎåÄÍ∏∞ÏãúÍ∞Ñ Îã®Ï∂ï',
          'ÏÉÅÎã¥ÏÇ¨ Îß§Ïπ≠ Í∞úÏÑ†'
        ],
        detailFeedback: [
          { rating: 5, comment: 'ÏÉÅÎã¥ÏÇ¨ ÏÑ†ÏÉùÎãòÏù¥ Ï†ïÎßê ÎèÑÏõÄÏù¥ ÎêòÏóàÏäµÎãàÎã§. Í∞êÏÇ¨Ìï©ÎãàÎã§.' },
          { rating: 4, comment: 'ÏòàÏïΩÏù¥ Ï°∞Í∏à Ïñ¥Î†§Ïõ†ÏßÄÎßå ÏÉÅÎã¥ ÏûêÏ≤¥Îäî ÎßåÏ°±Ïä§Îü¨Ïõ†Ïñ¥Ïöî.' },
          { rating: 5, comment: 'ÏßÅÏû• ÎÇ¥ Ïä§Ìä∏Î†àÏä§ Í¥ÄÎ¶¨Ïóê ÌÅ∞ ÎèÑÏõÄÏù¥ ÎêòÏóàÏäµÎãàÎã§.' }
        ],
        generatedAt: new Date(),
        reportType: 'satisfaction'
      };
      setCurrentReport(demoReport);
      setReportType('satisfaction');
      setShowReportModal(true);
    }
  };

  const generateCostReport = async (selectedYear: string) => {
    try {
      const token = localStorage.getItem('token');
      const year = selectedYear === 'ÏÇ¨Ïö©Ïûê Ï†ïÏùò' ? new Date().getFullYear() : parseInt(selectedYear.replace('ÎÖÑ', ''));
      const response = await axios.post('http://localhost:3000/api/company-admin/reports/cost', {
        year
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCurrentReport(response.data.report);
      setReportType('cost');
      setShowReportModal(true);
    } catch (error) {
      console.error('ÎπÑÏö© Î¶¨Ìè¨Ìä∏ ÏÉùÏÑ± Ïã§Ìå®:', error);
      // Îç∞Î™® Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
      const year = selectedYear === 'ÏÇ¨Ïö©Ïûê Ï†ïÏùò' ? new Date().getFullYear() : parseInt(selectedYear.replace('ÎÖÑ', ''));
      const currentEmployeeCount = 45;
      const totalSessions = 156;
      const totalCost = 12480000;
      const avgSalary = 50000000;
      const dailyProductivity = 200000;

      // Îçî ÌòÑÏã§Ï†ÅÏù∏ ROI Í≥ÑÏÇ∞
      const absenteeismReduction = totalSessions * 0.5; // 78Ïùº
      const absenteeismSavings = absenteeismReduction * dailyProductivity; // 15,600,000Ïõê
      const turnoverReduction = 1.35; // 15% * 0.2 * 45Î™Ö
      const turnoverSavings = turnoverReduction * 10000000; // 13,500,000Ïõê
      const productivityGain = totalSessions * (avgSalary * 0.03 / currentEmployeeCount) * 12; // 51,999,960Ïõê
      const medicalSavings = (totalSessions / currentEmployeeCount) * (avgSalary * 0.05) * 0.15; // 2,600,000Ïõê
      const totalBenefits = absenteeismSavings + turnoverSavings + productivityGain + medicalSavings;

      const demoReport = {
        year,
        totalSessions,
        totalCost,
        avgSessionCost: 80000,
        monthlyCosts: {
          1: { month: 1, sessions: 12, cost: 960000 },
          2: { month: 2, sessions: 15, cost: 1200000 },
          3: { month: 3, sessions: 18, cost: 1440000 },
          4: { month: 4, sessions: 14, cost: 1120000 },
          5: { month: 5, sessions: 16, cost: 1280000 },
          6: { month: 6, sessions: 13, cost: 1040000 },
          7: { month: 7, sessions: 11, cost: 880000 },
          8: { month: 8, sessions: 19, cost: 1520000 },
